@article{allison_etal2021effectsilica,
  title = {The Effect of Silica Desiccation under Different Storage Conditions on Filter-Immobilized Environmental {{DNA}}},
  author = {Allison, Michael J. and Round, Jessica M. and Bergman, Lauren C. and Mirabzadeh, Ali and Allen, Heather and Weir, Aron and Helbing, Caren C.},
  year = {2021},
  month = dec,
  journal = {BMC Research Notes},
  volume = {14},
  number = {1},
  pages = {106},
  issn = {1756-0500},
  doi = {10.1186/s13104-021-05530-x},
  url = {https://bmcresnotes.biomedcentral.com/articles/10.1186/s13104-021-05530-x},
  urldate = {2024-11-01},
  abstract = {Objective:{\enspace} Silica gel beads have promise as a non-toxic, cost-effective, portable method for storing environmental DNA (eDNA) immobilized on filter membranes. Consequently, many ecological surveys are turning to silica bead filter desiccation rather than ethanol preservation. However, no systematic evaluation of silica bead storage conditions or duration past 1 week has been published. The present study evaluates the quality of filter-immobilized eDNA desiccated with silica gel under different storage conditions for over a year using targeted quantitative real-time polymerase chain reaction (qPCR)-based assays. Results:{\enspace} While the detection of relatively abundant eDNA target was stable over 15 months from either ethanol- or silica gel-preserved filters at - 20 and 4 {$^\circ$}C, silica gel out-performed ethanol preservation at 23 {$^\circ$}C by preventing a progressive decrease in eDNA sample quality. Silica gel filter desiccation preserved low abundance eDNA equally well up to 1 month regardless of storage temperature (18, 4, or - 20 {$^\circ$}C). However only storage at - 20 {$^\circ$}C prevented a noticeable decrease in detectability at 5 and 12 months. The results indicate that brief storage of eDNA filters with silica gel beads up to 1 month can be successfully accomplished at a range of temperatures. However, longer-term storage should be at - 20 {$^\circ$}C to maximize sample integrity.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/B8MT7HCK/Allison et al. - 2021 - The effect of silica desiccation under different s.pdf}
}

