@article{anderson_etal1997UseClove,
  title = {The {{Use}} of {{Clove Oil}} as an {{Anesthetic}} for {{Rainbow Trout}} and {{Its Effects}} on {{Swimming Performance}}},
  author = {Anderson, W. Gary and McKinley, R. Scott and Colavecchia, Maria},
  year = {1997},
  journal = {North American Journal of Fisheries Management},
  volume = {17},
  number = {2},
  pages = {301--307},
  issn = {1548-8675},
  doi = {10.1577/1548-8675(1997)017<0301:TUOCOA>2.3.CO;2},
  url = {https://onlinelibrary.wiley.com/doi/abs/10.1577/1548-8675%281997%29017%3C0301%3ATUOCOA%3E2.3.CO%3B2},
  urldate = {2024-11-05},
  abstract = {The only anesthetic registered in North America for use in fisheries science is 3-aminobenzoic acid ethyl ester methanesulfate (tricaine or MS-222). Although MS-222 is a very effective anesthesia for several fish species, its application in the field is limited because U.S. Food and Drug Administration guidelines demand a 21-d withdrawal period after exposure to MS-222 before fish can be released and enter the food chain. As a consequence, carbon dioxide (CO2) has been used as a substitute anesthetic; however, induction and recovery times with CO2 are long, and anesthesia is shallow in comparison with MS-222. We compared the efficacy of MS-222 to that of clove oil, a naturally occurring substance, for use as an anesthetic for juvenile and adult rainbow trout Onchorhynchus mykiss. Clove oil was as effective as MS-222 in inducing anesthesia in both age-groups. Furthermore, exposure to either clove oil or MS-222 at the concentrations tested was not detrimental to critical swimming speed of juvenile or adult rainbow trout. We propose that clove oil be considered as an alternative to MS-222 for use as a fish anesthetic.},
  copyright = {{\copyright} 1997 American Fisheries Society},
  langid = {english}
}

