@article{bakker_etal2017EnvironmentalDNA,
  title = {Environmental {{DNA}} Reveals Tropical Shark Diversity in Contrasting Levels of Anthropogenic Impact},
  author = {Bakker, Judith and Wangensteen, Owen S. and Chapman, Demian D. and Boussarie, Germain and Buddo, Dayne and Guttridge, Tristan L. and Hertler, Heidi and Mouillot, David and Vigliola, Laurent and Mariani, Stefano},
  year = {2017},
  month = dec,
  journal = {Scientific Reports},
  volume = {7},
  number = {1},
  pages = {16886},
  issn = {2045-2322},
  doi = {10.1038/s41598-017-17150-2},
  url = {https://www.nature.com/articles/s41598-017-17150-2},
  urldate = {2024-11-01},
  abstract = {Abstract             Sharks are charismatic predators that play a key role in most marine food webs. Their demonstrated vulnerability to exploitation has recently turned them into flagship species in ocean conservation. Yet, the assessment and monitoring of the distribution and abundance of such mobile species in marine environments remain challenging, often invasive and resource-intensive. Here we pilot a novel, rapid and non-invasive environmental DNA (eDNA) metabarcoding approach specifically targeted to infer shark presence, diversity and eDNA read abundance in tropical habitats. We identified at least 21 shark species, from both Caribbean and Pacific Coral Sea water samples, whose geographical patterns of diversity and read abundance coincide with geographical differences in levels of anthropogenic pressure and conservation effort. We demonstrate that eDNA metabarcoding can be effectively employed to study shark diversity. Further developments in this field have the potential to drastically enhance our ability to assess and monitor elusive oceanic predators, and lead to improved conservation strategies.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/XUBG5AHY/Bakker et al. - 2017 - Environmental DNA reveals tropical shark diversity.pdf}
}

@article{baldwin2015Potentialmitigation,
  title = {Potential Mitigation of and Adaptation to Climate-Driven Changes in {{California}}'s Highlands through Increased Beaver Populations},
  author = {Baldwin, Jeff},
  year = {2015},
  journal = {CALIFORNIA FISH AND GAME},
  volume = {101},
  number = {4},
  abstract = {Climate models forecast significant changes in California's temperature and precipitation patterns. Those changes are likely to affect fluvial and riparian habitat. Across the American West several researchers and civil society groups promote increased beaver (Castor canadensis) presence as a means to moderate such changes. This study reviews three literatures in an effort to evaluate the potential for beaver to adapt to and to mitigate anticipated changes in California's higher elevation land- and waterscapes. First, I provide a synopsis of modeled changes in temperatures and precipitation. Forecasts agree that temperatures will continue to increase, to 1.5--4.0{$^\circ$} C by 2060; however, forecasts for precipitation are more variable in sign and among models. Second, researchers anticipate climate-driven changes in stream and riparian areas and project that snowpacks and summer flows will continue to decline, winter and spring flood magnitudes will increase, spring stream recession will likely continue to occur earlier and more quickly, and highland fires will be more extensive. Each of these changes has important implications for wildlife and public lands managers. A third focus reviews beaver natural histories and finds that where beaver dams are persistent, they may sequester sediment and create wet meadows that can moderate floods, augment early summer baseflows, sequester carbon in soils and standing biomass, decrease ecological problems posed by earlier spring stream recession, and potentially help cool early summer and post-wildfire stream temperatures. However, due in part to currently limited habitat suitability and to conflicts with other human interests, mitigation would likely be most meaningful on local rather than statewide scales.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/JD68NUI7/baldwin_2015_potential_mitigation_of_and_adaptation_to_climate-driven_changes_in.pdf}
}

@article{barnes_etal2014EnvironmentalConditions,
  title = {Environmental {{Conditions Influence eDNA Persistence}} in {{Aquatic Systems}}},
  author = {Barnes, Matthew A. and Turner, Cameron R. and Jerde, Christopher L. and Renshaw, Mark A. and Chadderton, W. Lindsay and Lodge, David M.},
  year = {2014},
  month = feb,
  journal = {Environmental Science \& Technology},
  volume = {48},
  number = {3},
  pages = {1819--1827},
  issn = {0013-936X, 1520-5851},
  doi = {10.1021/es404734p},
  url = {https://pubs.acs.org/doi/10.1021/es404734p},
  urldate = {2024-11-01},
  abstract = {Environmental DNA (eDNA) surveillance holds great promise for improving species conservation and management. However, few studies have investigated eDNA dynamics under natural conditions, and interpretations of eDNA surveillance results are clouded by uncertainties about eDNA degradation. We conducted a literature review to assess current understanding of eDNA degradation in aquatic systems and an experiment exploring how environmental conditions can influence eDNA degradation. Previous studies have reported macrobial eDNA persistence ranging from less than 1 day to over 2 weeks, with no attempts to quantify factors affecting degradation. Using a SYBR Green quantitative PCR assay to observe Common Carp (Cyprinus carpio) eDNA degradation in laboratory mesocosms, our rate of Common Carp eDNA detection decreased over time. Common Carp eDNA concentration followed a pattern of exponential decay, and observed decay rates exceeded previously published values for aquatic macrobial eDNA. Contrary to our expectations, eDNA degradation rate declined as biochemical oxygen demand, chlorophyll, and total eDNA (i.e., from any organism) concentration increased. Our results help explain the widely divergent, previously published estimates for eDNA degradation. Measurements of local environmental conditions, consideration of environmental influence on eDNA detection, and quantification of local eDNA degradation rates will help interpret future eDNA surveillance results.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/R882V3XX/Barnes et al. - 2014 - Environmental Conditions Influence eDNA Persistenc.pdf}
}

