@article{calvignac-spencer_etal2013Carrionflyderived,
  title = {Carrion Fly-derived {{{\textsc{DNA}}}} as a Tool for Comprehensive and Cost-effective Assessment of Mammalian Biodiversity},
  shorttitle = {Carrion Fly-derived},
  author = {Calvignac-Spencer, S{\'e}bastien and Merkel, Kevin and Kutzner, Nadine and K{\"u}hl, Hjalmar and Boesch, Christophe and Kappeler, Peter M. and Metzger, Sonja and Schubert, Grit and Leendertz, Fabian H.},
  year = {2013},
  month = feb,
  journal = {Molecular Ecology},
  volume = {22},
  number = {4},
  pages = {915--924},
  issn = {0962-1083, 1365-294X},
  doi = {10.1111/mec.12183},
  url = {https://onlinelibrary.wiley.com/doi/10.1111/mec.12183},
  urldate = {2024-11-01},
  abstract = {Large-scale monitoring schemes are essential in assessing global mammalian biodiversity, and in this framework, leeches have recently been promoted as an indirect source of DNA from terrestrial mammal species. Carrion feeding flies are ubiquitous and can be expected to feed on many vertebrate carcasses. Hence, we tested whether fly-derived DNA analysis may also serve as a novel tool for mammalian diversity surveys. We screened DNA extracted from 201 carrion flies collected in tropical habitats of Co{\textasciicircum}te d'Ivoire and Madagascar for mammal DNA using multiple PCR systems and retrieved DNA sequences from a diverse set of species (22 in Co{\textasciicircum}te d'Ivoire, four in Madagascar) exploiting distinct forest strata and displaying a broad range of body sizes. Deep sequencing of amplicons generated from pools of flies performed equally well as individual sequencing approaches. We conclude that the analysis of fly-derived DNA can be implemented in a very rapid and cost-effective manner and will give a relatively unbiased picture of local mammal diversity. Carrion flies therefore represent an extraordinary and thus far unexploited resource of mammal DNA, which will probably prove useful for future inventories of wild mammal communities.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/86A325QH/Calvignac‐Spencer et al. - 2013 - Carrion fly‐derived span style=font-variantsmal.pdf}
}

@article{calvignac-spencer_etal2013invertebratestomachs,
  title = {An Invertebrate Stomach's View on Vertebrate Ecology: {{Certain}} Invertebrates Could Be Used as ``Vertebrate Samplers'' and Deliver {{DNA}}-based Information on Many Aspects of Vertebrate Ecology},
  shorttitle = {An Invertebrate Stomach's View on Vertebrate Ecology},
  author = {Calvignac-Spencer, S{\'e}bastien and Leendertz, Fabian H. and Gilbert, M. Thomas P. and Schubert, Grit},
  year = {2013},
  month = nov,
  journal = {BioEssays},
  volume = {35},
  number = {11},
  pages = {1004--1013},
  issn = {0265-9247, 1521-1878},
  doi = {10.1002/bies.201300060},
  url = {https://onlinelibrary.wiley.com/doi/10.1002/bies.201300060},
  urldate = {2024-11-01},
  abstract = {Recent studies suggest that vertebrate genetic material ingested by invertebrates (iDNA) can be used to investigate vertebrate ecology. Given the ubiquity of invertebrates that feed on vertebrates across the globe, iDNA might qualify as a very powerful tool for 21st century population and conservation biologists. Here, we identify some invertebrate characteristics that will likely influence iDNA retrieval and elaborate on the potential uses of invertebrate-derived information. We hypothesize that beyond inventorying local faunal diversity, iDNA should allow for more profound insights into wildlife population density, size, mortality, and infectious agents. Based on the similarities of iDNA with other low-quality sources of DNA, a general technical framework for iDNA analyses is proposed. As it is likely that no such thing as a single ideal iDNA sampler exists, forthcoming research efforts should aim at cataloguing invertebrate properties relevant to iDNA retrieval so as to guide future usage of the invertebrate tool box.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/M3JS3SXV/Calvignac‐Spencer et al. - 2013 - An invertebrate stomach's view on vertebrate ecolo.pdf}
}

