  url = {https://cdnsciencepub.com/doi/10.1139/cjfas-2020-0247},
  urldate = {2022-01-04},
  abstract = {Estuaries represent a transition zone for salmon migrating from fresh water to marine waters, yet their contribution to juvenile growth is poorly quantified. Here, we use genetic stock identification and otolith analyses to quantify estuarine habitat use by Chinook salmon (Oncorhynchus tshawytscha) --- the Pacific salmon species considered most reliant on this habitat --- in Canada's most productive salmon river, the Fraser River. Two years of sampling revealed subyearling migrant (ocean-type) Chinook from the Harrison River to be the estuary's dominant salmon population throughout the emigration period. These Chinook salmon were caught predominantly in the estuary's brackish marshes but shifted to more saline habitats as they grew. Otolith analyses indicated that these Chinook salmon have wide-ranging entry timing (from February to May) and longer estuarine residency (weeks to months, mean 41.8 days) than estimated by prior studies, but similar daily growth rates (mean 6 SD: 0.57 6 0.13 mm) across entry dates and residency periods, implying sufficient foraging opportunities throughout the emigration period and habitats. Together, these results suggest that estuarine habitat is more important for early marine growth of subyearling migrant Chinook salmon than previously recognized.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/JZKU8DVJ/chalifour_et_al_2021_chinook_salmon_exhibit_long-term_rearing_and_early_marine_growth_in_the_fraser.pdf}
}

@article{chambert_etal2018analyticalframework,
  title = {An Analytical Framework for Estimating Aquatic Species Density from Environmental {{{\textsc{DNA}}}}},
  shorttitle = {An Analytical Framework for Estimating Aquatic Species Density from Environmental},
  author = {Chambert, Thierry and Pilliod, David S. and Goldberg, Caren S. and Doi, Hideyuki and Takahara, Teruhiko},
  year = {2018},
  month = mar,
  journal = {Ecology and Evolution},
  volume = {8},
  number = {6},
  pages = {3468--3477},
  issn = {2045-7758, 2045-7758},
  doi = {10.1002/ece3.3764},
  url = {https://onlinelibrary.wiley.com/doi/10.1002/ece3.3764},
  urldate = {2024-11-01},
  abstract = {Environmental DNA (eDNA) analysis of water samples is on the brink of becoming a standard monitoring method for aquatic species. This method has improved detection rates over conventional survey methods and thus has demonstrated effectiveness for estimation of site occupancy and species distribution. The frontier of eDNA applications, however, is to infer species density. Building upon previous studies, we present and assess a modeling approach that aims at inferring animal density from eDNA. The modeling combines eDNA and animal count data from a subset of sites to estimate species density (and associated uncertainties) at other sites where only eDNA data are available. As a proof of concept, we first perform a cross-\-validation study using experimental data on carp in mesocosms. In these data, fish densities are known without error, which allows us to test the performance of the method with known data. We then evaluate the model using field data from a study on a stream salamander species to assess the potential of this method to work in natural settings, where density can never be known with absolute certainty. Two alternative distributions (Normal and Negative Binomial) to model variability in eDNA concentration data are assessed. Assessment based on the proof of concept data (carp) revealed that the Negative Binomial model provided much more accurate estimates than the model based on a Normal distribution, likely because eDNA data tend to be overdispersed. Greater imprecision was found when we applied the method to the field data, but the Negative Binomial model still provided useful density estimates. We call for further model development in this direction, as well as further research targeted at sampling design optimization. It will be important to assess these approaches on a broad range of study systems.},
  copyright = {http://creativecommons.org/licenses/by/4.0/},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/LHSLK99K/Chambert et al. - 2018 - An analytical framework for estimating aquatic spe.pdf}
}

