@article{cristescu_hebert2018UsesMisusesa,
  title = {Uses and {{Misuses}} of {{Environmental DNA}} in {{Biodiversity Science}} and {{Conservation}}},
  author = {Cristescu, Melania E. and Hebert, Paul D.N.},
  year = {2018},
  month = nov,
  journal = {Annual Review of Ecology, Evolution, and Systematics},
  volume = {49},
  number = {1},
  pages = {209--230},
  issn = {1543-592X, 1545-2069},
  doi = {10.1146/annurev-ecolsys-110617-062306},
  url = {https://www.annualreviews.org/doi/10.1146/annurev-ecolsys-110617-062306},
  urldate = {2024-11-01},
  abstract = {The study of environmental DNA (eDNA) has the potential to revolutionize biodiversity science and conservation action by enabling the census of species on a global scale in near-real time. To achieve this promise, technical challenges must be resolved. In this review, we explore the main uses of eDNA as well as the complexities introduced by its misuse. Current eDNA methods require refinement and improved calibration and validation along the entire workflow to lessen false positives/negatives. Moreover, there is great need for a better understanding of the ``natural history'' of eDNA---its origins, state, lifetime, and transportation---and for more detailed insights concerning the physical and ecological limitations of eDNA use. Although eDNA analysis can provide powerful information, particularly in freshwater and marine environments, its impact is likely to be less significant in terrestrial settings. The broad adoption of eDNA tools in conservation will largely depend on addressing current uncertainties in data interpretation.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/JC79IQ5V/Cristescu and Hebert - 2018 - Uses and Misuses of Environmental DNA in Biodivers.pdf}
}

