@article{dejean_etal2011PersistenceEnvironmental,
  title = {Persistence of {{Environmental DNA}} in {{Freshwater Ecosystems}}},
  author = {Dejean, Tony and Valentini, Alice and Duparc, Antoine and {Pellier-Cuit}, St{\'e}phanie and Pompanon, Fran{\c c}ois and Taberlet, Pierre and Miaud, Claude},
  editor = {Gilbert, Jack Anthony},
  year = {2011},
  month = aug,
  journal = {PLoS ONE},
  volume = {6},
  number = {8},
  pages = {e23398},
  issn = {1932-6203},
  doi = {10.1371/journal.pone.0023398},
  url = {https://dx.plos.org/10.1371/journal.pone.0023398},
  urldate = {2024-11-01},
  abstract = {The precise knowledge of species distribution is a key step in conservation biology. However, species detection can be extremely difficult in many environments, specific life stages and in populations at very low density. The aim of this study was to improve the knowledge on DNA persistence in water in order to confirm the presence of the focus species in freshwater ecosystems. Aquatic vertebrates (fish: Siberian sturgeon and amphibian: Bullfrog tadpoles) were used as target species. In control conditions (tanks) and in the field (ponds), the DNA detectability decreases with time after the removal of the species source of DNA. DNA was detectable for less than one month in both conditions. The density of individuals also influences the dynamics of DNA detectability in water samples. The dynamics of detectability reflects the persistence of DNA fragments in freshwater ecosystems. The short time persistence of detectable amounts of DNA opens perspectives in conservation biology, by allowing access to the presence or absence of species e.g. rare, secretive, potentially invasive, or at low density. This knowledge of DNA persistence will greatly influence planning of biodiversity inventories and biosecurity surveys.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/SGDWJQFI/Dejean et al. - 2011 - Persistence of Environmental DNA in Freshwater Eco.pdf}
}

@article{dejean_etal2012Improveddetection,
  title = {Improved Detection of an Alien Invasive Species through Environmental {{DNA}} Barcoding: The Example of the {{American}} Bullfrog {{{\emph{Lithobates}}}}{\emph{ Catesbeianus}}},
  shorttitle = {Improved Detection of an Alien Invasive Species through Environmental {{DNA}} Barcoding},
  author = {Dejean, Tony and Valentini, Alice and Miquel, Christian and Taberlet, Pierre and Bellemain, Eva and Miaud, Claude},
  year = {2012},
  month = aug,
  journal = {Journal of Applied Ecology},
  volume = {49},
  number = {4},
  pages = {953--959},
  issn = {0021-8901, 1365-2664},
  doi = {10.1111/j.1365-2664.2012.02171.x},
  url = {https://besjournals.onlinelibrary.wiley.com/doi/10.1111/j.1365-2664.2012.02171.x},
  urldate = {2024-11-01},
  abstract = {Summary                            1.               {\enspace}Alien invasive species (AIS) are one of the major causes of biodiversity loss and global homogenization. Once an AIS becomes established, costs of control can be extremely high and complete eradication is not always achieved. The ability to detect a species at a low density greatly improves the success of eradication and decreases both the costs of control and the impact on ecosystems.                                         2.               {\enspace}In this study, we compare the sensitivity of traditional field methods, based on auditory and visual encounter surveys, with an environmental DNA (eDNA) survey for the detection of the American bullfrog               Rana catesbeiana               {$\quad$}={$\quad$}               Lithobates catesbeianus               , which is invasive in south-western France.                                         3.               {\enspace}We demonstrate that the eDNA method is valuable for species detection and surpasses traditional amphibian survey methods in terms of sensitivity and sampling effort. The bullfrog was detected in 38 sites using the molecular method, compared with seven sites using the diurnal and nocturnal surveys, suggesting that traditional field surveys have strongly underestimated the distribution of the American bullfrog.                                         4.               {\enspace}               Synthesis and applications               . The environmental DNA approach permits the early detection of alien invasive species (AIS), at very low densities and at any life stage, which is particularly important for the detection of rare and/or secretive aquatic species. This method can also be used to confirm the sensitivity of control operations and to better identify the distributions of vulnerable species, making this a very relevant tool for species inventory and management.                        ,              The environmental DNA approach permits the early detection of alien invasive species (AIS), at very low densities and at any life stage, which is particularly important for the detection of rare and/or secretive aquatic species. This method can also be used to confirm the sensitivity of control operations and to better identify the distributions of vulnerable species, making this a very relevant tool for species inventory and management.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/IM2CTUN6/Dejean et al. - 2012 - Improved detection of an alien invasive species th.pdf}
}

