@article{dysthe_etal2018Repurposingenvironmental,
  title = {Repurposing Environmental {{{\textsc{DNA}}}} Samples---Detecting the Western Pearlshell ( {{{\emph{Margaritifera}}}}{\emph{ Falcata}} ) as a Proof of Concept},
  shorttitle = {Repurposing Environmental},
  author = {Dysthe, Joseph C. and Rodgers, Torrey and Franklin, Thomas W. and Carim, Kellie J. and Young, Michael K. and McKelvey, Kevin S. and Mock, Karen E. and Schwartz, Michael K.},
  year = {2018},
  month = mar,
  journal = {Ecology and Evolution},
  volume = {8},
  number = {5},
  pages = {2659--2670},
  issn = {2045-7758, 2045-7758},
  doi = {10.1002/ece3.3898},
  url = {https://onlinelibrary.wiley.com/doi/10.1002/ece3.3898},
  urldate = {2024-11-01},
  abstract = {Information on the distribution of multiple species in a common landscape is fundamental to effective conservation and management. However, distribution data are expensive to obtain and often limited to high-\-profile species in a system. A recently developed technique, environmental DNA (eDNA) sampling, has been shown to be more sensitive than traditional detection methods for many aquatic species. A second and perhaps underappreciated benefit of eDNA sampling is that a sample originally collected to determine the presence of one species can be re-\-analyzed to detect additional taxa without additional field effort. We developed an eDNA assay for the western pearlshell mussel (Margaritifera falcata) and evaluated its effectiveness by analyzing previously collected eDNA samples that were annotated with information including sample location and deposited in a central repository. The eDNA samples were initially collected to determine habitat occupancy by nonbenthic fish species at sites that were in the vicinity of locations recently occupied by western pearlshell. These repurposed eDNA samples produced results congruent with historical western pearlshell surveys and permitted a more precise delineation of the extent of local populations. That a sampling protocol designed to detect fish was also successful for detecting a freshwater mussel suggests that rapidly accumulating collections of eDNA samples can be repurposed to enhance the efficiency and cost-\-effectiveness of aquatic biodiversity monitoring.},
  copyright = {http://creativecommons.org/licenses/by/4.0/},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/3QMFRZ3J/Dysthe et al. - 2018 - Repurposing environmental span style=font-varian.pdf}
}

