@article{eiler_etal2018EnvironmentalDNA,
  title = {Environmental {{DNA}} ({{eDNA}}) Detects the Pool Frog ({{Pelophylax}} Lessonae) at Times When Traditional Monitoring Methods Are Insensitive},
  author = {Eiler, Alexander and L{\"o}fgren, Anders and Hjerne, Olle and Nord{\'e}n, Sara and Saetre, Peter},
  year = {2018},
  month = apr,
  journal = {Scientific Reports},
  volume = {8},
  number = {1},
  pages = {5452},
  issn = {2045-2322},
  doi = {10.1038/s41598-018-23740-5},
  url = {https://www.nature.com/articles/s41598-018-23740-5},
  urldate = {2024-11-01},
  abstract = {Abstract                            Detection of endangered species is invaluable for conservation efforts, yet many traditional sampling techniques are ineffective at low population abundances or during certain periods of the year. Here, we compared results from a newly developed eDNA approach and the traditional observational method for the endangered pool frog (               Pelophylax lessonae               ). Analysis using an occupancy-modeling framework indicated that the probability of pools being occupied using eDNA (0.93) was higher than for the traditional method of counting calling males and silent observed individuals (0.72). Detailed analysis revealed complementarity among the methods. That is, the traditional method gave a high rate of observation in June, whereas eDNA gave at least as many or more observations during other parts of the year. Discrepancies among the methods depended on the dominant lifecycle stage, and eDNA concentrations were higher when juveniles were present than at times when spawning occurred. eDNA concentrations were also positively related to               P. lessonae               observations. Our study demonstrates that an eDNA protocol for monitoring of endangered amphibian species can be particularly valuable during periods when individuals are hard to detect by observational methods, and provides guidance to sampling efforts for research and monitoring programs in other regions and systems.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/9W5H7QN6/Eiler et al. - 2018 - Environmental DNA (eDNA) detects the pool frog (Pe.pdf}
}

