@article{evans_etal2017Fishcommunitya,
  title = {Fish Community Assessment with {{eDNA}} Metabarcoding: Effects of Sampling Design and Bioinformatic Filtering},
  shorttitle = {Fish Community Assessment with {{eDNA}} Metabarcoding},
  author = {Evans, Nathan T. and Li, Yiyuan and Renshaw, Mark A. and Olds, Brett P. and Deiner, Kristy and Turner, Cameron R. and Jerde, Christopher L. and Lodge, David M. and Lamberti, Gary A. and Pfrender, Michael E.},
  year = {2017},
  month = sep,
  journal = {Canadian Journal of Fisheries and Aquatic Sciences},
  volume = {74},
  number = {9},
  pages = {1362--1374},
  issn = {0706-652X, 1205-7533},
  doi = {10.1139/cjfas-2016-0306},
  url = {http://www.nrcresearchpress.com/doi/10.1139/cjfas-2016-0306},
  urldate = {2024-11-01},
  abstract = {Species richness is a metric of biodiversity that represents the number of species present in a community. Traditional fisheries assessments that rely on capture of organisms often underestimate true species richness. Environmental DNA (eDNA) metabarcoding is an alternative tool that infers species richness by collecting and sequencing DNA present in the ecosystem. Our objective was to determine how spatial distribution of samples and ``bioinformatic stringency'' affected eDNA-metabarcoding estimates of species richness compared with capture-based estimates in a 2.2 ha reservoir. When bioinformatic criteria required species to be detected only in a single sample, eDNA metabarcoding detected all species captured with traditional methods plus an additional 11 noncaptured species. However, when we required species to be detected with multiple markers and in multiple samples, eDNA metabarcoding detected only seven of the captured species. Our analysis of the spatial patterns of species detection indicated that eDNA was distributed relatively homogeneously throughout the reservoir, except near the inflowing stream. We suggest that interpretation of eDNA metabarcoding data must consider the potential effects of water body type, spatial resolution, and bioinformatic stringency.},
  copyright = {http://www.nrcresearchpress.com/page/about/CorporateTextAndDataMining},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/84JPQ62P/Evans et al. - 2017 - Fish community assessment with eDNA metabarcoding.pdf}
}

