@article{favaro_olszynski2017Authorizednet,
  title = {Authorized Net Losses of Fish Habitat Demonstrate Need for Improved Habitat Protection in {{Canada}}},
  author = {Favaro, Brett and Olszynski, Martin},
  year = {2017},
  month = mar,
  journal = {Canadian Journal of Fisheries and Aquatic Sciences},
  volume = {74},
  number = {3},
  pages = {285--291},
  issn = {0706-652X, 1205-7533},
  doi = {10.1139/cjfas-2016-0480},
  url = {http://www.nrcresearchpress.com/doi/10.1139/cjfas-2016-0480},
  urldate = {2021-11-23},
  abstract = {Fish habitat is essential to the stability and productivity of fisheries. In Canada, the primary legal tool for protecting fish habitat is the federal Fisheries Act. In 2012, this law was changed to narrow the scope of habitat protection. The government's position was that the previous regime went beyond what was necessary to protect fish and fish habitat. Here, we tested that assertion by examining Fisheries Act authorizations to harmfully alter, disrupt, or destroy fish habitat issued by Fisheries and Oceans Canada during a 6-month period in 2012, obtained using access to information processes. We found the majority of projects (67\%) were authorized to impact more habitat than proponents were required to compensate for, likely resulting in a net loss of fish habitat. Our analysis show an aggregate net loss --- defined as authorized impact minus required compensation --- of 2\,919\,143 m               2               authorized across 78 projects. Drawing from these results, we present four recommendations for an improved habitat protection regime under a renewed Fisheries Act, emphasizing the need to establish a public registry for authorizations and monitoring data.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/I3CTNLVR/favaro_olszynski_2017_authorized_net_losses_of_fish_habitat_demonstrate_need_for_improved_habitat.pdf}
}

@article{fernandes_etal2017efficacyclove,
  title = {The Efficacy of Clove Oil as an Anaesthetic and in Euthanasia Procedure for Small-Sized Tropical Fishes},
  author = {Fernandes, I. M. and Bastos, Y. F. and Barreto, D. S. and Louren{\c c}o, L. S. and Penha, J. M.},
  year = {2017},
  journal = {Brazilian Journal of Biology = Revista Brasleira De Biologia},
  volume = {77},
  number = {3},
  pages = {444--450},
  issn = {1678-4375},
  doi = {10.1590/1519-6984.15015},
  abstract = {Clove oil is used as a fish anesthetic because it is a natural and inexpensive product with low toxicity risks. The goal of the present study was to determine the appropriate concentration of clove oil for small-sized tropical fish to be used in mark-recapture studies or when individuals are to be sacrificed. We applied three different clove oil concentrations (D1=0.05 mL, D2=0.10 mL and D3=0.20 mL per 500 mL of water) on three small-sized fish species. We found a negative relationship between induction time and treatment for two species (Hyphessobrycon sp.1 and Hemigrammus sp.), while concentration was unrelated to recovery time. Fish body length was positively related to induction time in the D2 treatment for Hemigrammus sp., and negatively for Hyphessobrycon sp.1 in the D1 treatment, but was unrelated to recovery time for three species and treatments. Mortality rates varied across treatments, but higher rates were observed with higher clove oil concentrations. We conclude that 0.05 mL of clove oil per 500 mL of water is the most efficient dose for studies where fish will be released back to their natural habitats, while 0.20 mL of clove oil is recommended for studies that require fish euthanization for further laboratory analyses.},
  langid = {english},
  pmid = {27683808},
  keywords = {Anesthesia,Animals,Clove Oil,Euthanasia,Fishes}
}

@article{ficetola_etal2008Speciesdetectiona,
  title = {Species Detection Using Environmental {{DNA}} from Water Samples},
  author = {Ficetola, Gentile Francesco and Miaud, Claude and Pompanon, Fran{\c c}ois and Taberlet, Pierre},
  year = {2008},
  month = aug,
  journal = {Biology Letters},
  volume = {4},
  number = {4},
  pages = {423--425},
  issn = {1744-9561, 1744-957X},
  doi = {10.1098/rsbl.2008.0118},
  url = {https://royalsocietypublishing.org/doi/10.1098/rsbl.2008.0118},
  urldate = {2024-11-01},
  abstract = {The assessment of species distribution is a first critical phase of biodiversity studies and is necessary to many disciplines such as biogeography, conservation biology and ecology. However, several species are difficult to detect, especially during particular time periods or developmental stages, potentially biasing study outcomes. Here we present a novel approach, based on the limited persistence of DNA in the environment, to detect the presence of a species in fresh water. We used specific primers that amplify short mitochondrial DNA sequences to track the presence of a frog (               Rana catesbeiana               ) in controlled environments and natural wetlands. A multi-sampling approach allowed for species detection in all environments where it was present, even at low densities. The reliability of the results was demonstrated by the identification of amplified DNA fragments, using traditional sequencing and parallel pyrosequencing techniques. As the environment can retain the molecular imprint of inhabiting species, our approach allows the reliable detection of secretive organisms in wetlands without direct observation. Combined with massive sequencing and the development of DNA barcodes that enable species identification, this approach opens new perspectives for the assessment of current biodiversity from environmental samples.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/IV8KBNU3/Ficetola et al. - 2008 - Species detection using environmental DNA from wat.pdf}
}

