@article{fischer_etal2000IMPROVINGRIPARIAN,
  title = {{{IMPROVING RIPARIAN BUFFER STRIPS AND CORRIDORS FOR WATER QUALITY AND WILDLIFE}}},
  author = {Fischer, Richard A and Martin, Chester O and Fischenich, J Craig},
  year = {2000},
  abstract = {The management and restoration of riparian zones has received considerable attention throughout the United States. Numerous studies have shown that riparian buffer strips of sufficient width protect and improve water quality by intercepting non-point source pollutants. Buffer strips also clearly provide a diversity of other functions, including movement corridors and habitat for a large variety of organisms. However, criteria for determining proper dimensions of buffer strips for most ecological functions are not well established. Although riparian zones are being restored along thousands of streambank miles throughout the country, the ecological benefits of variable buffer strip designs (e.g., width, length, vegetation type, placement within the watershed) have not been adequately recognized. There have been few systematic attempts to establish criteria that mesh water quality width requirements with other riparian functions. Subsequently, management prescriptions (e.g., width recommendations) are frequently based upon anecdotal information with little regard for the full range of effects these decisions may have on other riparian functions. Our objectives are to address the suitability of riparian zones to protect water quality while enhancing biodiversity, and to discuss recent strides in providing improved guidance for corridor and buffer designs based primarily on ecological criteria.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/2XD7M6W8/Fischer et al. - IMPROVING RIPARIAN BUFFER STRIPS AND CORRIDORS FOR.pdf}
}

