@article{fukumoto_etal2015basinscaleapplication,
  title = {A Basin-scale Application of Environmental {{{\textsc{DNA}}}} Assessment for Rare Endemic Species and Closely Related Exotic Species in Rivers: A Case Study of Giant Salamanders in {{Japan}}},
  shorttitle = {A Basin-scale Application of Environmental},
  author = {Fukumoto, Sou and Ushimaru, Atushi and Minamoto, Toshifumi},
  editor = {Crispo, Erika},
  year = {2015},
  month = apr,
  journal = {Journal of Applied Ecology},
  volume = {52},
  number = {2},
  pages = {358--365},
  issn = {0021-8901, 1365-2664},
  doi = {10.1111/1365-2664.12392},
  url = {https://besjournals.onlinelibrary.wiley.com/doi/10.1111/1365-2664.12392},
  urldate = {2024-11-01},
  abstract = {Summary                                                                To prevent the invasion of exotic species causing a decline in an endangered endemic species, it is important to determine the distribution of both species at an early stage, when the density of the exotic species is still low, and to manage the invasion immediately. However, distinguishing between closely related species is difficult because they share similar characteristics.                                                                          The identification of                     DNA                     fragments sampled from a body of water (environmental                     DNA                     ) has become a popular technique for rapidly determining the distribution of a target species. In this study, we analysed environmental                     DNA                     in water samples from 37 sites across the Katsura River basin in Japan. We used TaqMan real-time                     PCR                     to distinguish the Japanese giant salamander                     Andrias japonicus                     from the closely related Chinese giant salamander                     Andrias davidianus                     , which is known to invade Japanese rivers and hybridize with the Japanese species.                                                                                             In environmental samples, we detected mt                     DNA                     of the endemic species at 25 sites and mt                     DNA                     of the exotic species at nine sites. The                     DNA                     detection sites were concentrated in the upstream region. The exotic species                     DNA                     was found beyond the limits of an earlier capturing survey.                                                                                             Synthesis and applications                     . Using environmental                     DNA                     to monitor the two salamander species requires less time and effort than traditional surveys, so a wide-ranging survey can be conducted rapidly. Our results showed that performing three environmental                     DNA                     surveys for each site between autumn and winter is desirable for giant salamanders. Further collection of environmental                     DNA                     , in combination with conventional population surveys, will provide valuable information that can help protect rare endemic species in a variety of aquatic ecosystems and can help monitor the invasion of exotic species.                                                                           ,                             Using environmental               DNA               to monitor the two salamander species requires less time and effort than traditional surveys, so a wide-ranging survey can be conducted rapidly. Our results showed that performing three environmental               DNA               surveys for each site between autumn and winter is desirable for giant salamanders. Further collection of environmental               DNA               , in combination with conventional population surveys, will provide valuable information that can help protect rare endemic species in a variety of aquatic ecosystems and can help monitor the invasion of exotic species.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/WGN6VK82/Fukumoto et al. - 2015 - A basin‐scale application of environmental span s.pdf}
}

