@article{furlan_etal2019eDNAsurveys,
  title = {{{eDNA}} Surveys to Detect Species at Very Low Densities: {{A}} Case Study of {{European}} Carp Eradication in {{Tasmania}}, {{Australia}}},
  shorttitle = {{{eDNA}} Surveys to Detect Species at Very Low Densities},
  author = {Furlan, Elise M. and Gleeson, Dianne and Wisniewski, Chris and Yick, Jonah and Duncan, Richard P.},
  editor = {Strecker, Angela},
  year = {2019},
  month = nov,
  journal = {Journal of Applied Ecology},
  volume = {56},
  number = {11},
  pages = {2505--2517},
  issn = {0021-8901, 1365-2664},
  doi = {10.1111/1365-2664.13485},
  url = {https://besjournals.onlinelibrary.wiley.com/doi/10.1111/1365-2664.13485},
  urldate = {2024-11-01},
  abstract = {Abstract                                                                Eradications of invasive species are usually expensive and difficult to conduct. Knowing when to declare an eradication successful requires distinguishing between failed detection of the target species due to imperfect sensitivity of the detection method and true species absence. This is difficult because the sensitivities of many detection methods are unknown.                                                     Environmental DNA (eDNA) methods can be used to detect species by analysing DNA present in environmental samples. eDNA has been promoted as a particularly sensitive and cost-effective way to detect species at low densities and, importantly, the sensitivity of eDNA surveys can be quantified. Nevertheless, the effort and costs involved in detecting species at extremely low densities, such as required during eradication, have not been previously calculated.                                                                          We evaluated the sensitivity of eDNA surveys in detecting the invasive European carp,                     Cyprinus carpio                     , in two lakes in Tasmania, Australia, one in which carp have been eradicated and a second in which carp are currently being eradicated. We determined the sampling effort and associated cost required to detect the species at very low density in these lakes.                                                                        While our eDNA survey detected the current low density carp population present in Lake Sorell, we show that an exponential increase in sampling effort and associated cost will be required to confidently detect the species as the population declines. Similarly, while our eDNA survey corroborated the species absence from Lake Crescent, our detection confidence was low. We quantify the survey effort and financial investment required to confidently establish eradication success in Lake Crescent.                                                                          Synthesis and applications                     . Estimating the environmental DNA (eDNA) survey effort and cost required to detect species at a given density will enable practitioners to make informed decisions on the feasibility of implementing such surveys. Quantifying the sensitivity of eDNA surveys will also inform the confidence practitioners should place in eDNA~detection~results to ensure appropriate management actions are implemented and provide a suitable stopping point at which to confidently declare eradication success.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/XT897WDQ/Furlan et al. - 2019 - eDNA surveys to detect species at very low densiti.pdf}
}

