@article{ghosal_etal2018AttractingCommon,
  title = {Attracting {{Common Carp}} to a Bait Site with Food Reveals Strong Positive Relationships between Fish Density, Feeding Activity, Environmental {{{\textsc{DNA}}}} , and Sex Pheromone Release That Could Be Used in Invasive Fish Management},
  shorttitle = {Attracting {{Common Carp}} to a Bait Site with Food Reveals Strong Positive Relationships between Fish Density, Feeding Activity, Environmental},
  author = {Ghosal, Ratna and Eichmiller, Jessica J. and Witthuhn, Bruce A. and Sorensen, Peter W.},
  year = {2018},
  month = jul,
  journal = {Ecology and Evolution},
  volume = {8},
  number = {13},
  pages = {6714--6727},
  issn = {2045-7758, 2045-7758},
  doi = {10.1002/ece3.4169},
  url = {https://onlinelibrary.wiley.com/doi/10.1002/ece3.4169},
  urldate = {2024-11-01},
  abstract = {Measurement of environmental DNA (eDNA) is becoming a common technique to survey for rare and invasive fish due to its sensitivity and specificity. However, its utility is limited by an incomplete understanding of factors governing its sources and fates. Failure to detect eDNA is especially difficult to interpret so surveillance techniques often collect large numbers of samples across broad regions. If, however, fish could be reliably attracted to a single location where their eDNA could be easily measured that would be useful. We conducted a proof-\-of-\-concept study of this idea using invasive Common Carp. We monitored the distribution of radio-\-tagged Carp and their eDNA across a 67 ha lake focusing at the bait site while a pheromone (Prostaglandin F2{$\alpha$}; PGF2{$\alpha$}) was also measured to determine their reproductive condition. Prior to baiting, Carp were patchily distributed and while eDNA was occasionally detectable, it was patchy and only loosely associated with moderately dense groups of fish. Further, neither Carp, nor their eDNA were consistently measurable at the bait site and surrounding region, and the pheromone was not measurable at all. However, once baiting commenced, Carp started visiting the bait site and feeding, especially at night, where eDNA levels increased 500-\-fold as fish densities doubled and PGF2{$\alpha$} became detectable. Fish presence, eDNA and pheromone concentrations peaked at night after 6 days, strongly suggesting feeding activity was the main driver. While the presence of eDNA precisely coincided with this aggregation, levels had dropped dramatically within 5 m. PGF2{$\alpha$} levels dropped less rapidly and demonstrated the presence of live mature fish. We suggest that food could be used to train fish to come to locations where they otherwise are too scarce to be reliably measured, increasing their eDNA release, making them measurable, and their reproductive condition also discernable by measuring pheromones.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/YYMCX9Y2/Ghosal et al. - 2018 - Attracting Common Carp to a bait site with food re.pdf}
}

