@article{gingera_etal2017EnvironmentalDNA,
  title = {Environmental {{DNA}} as a Detection Tool for Zebra Mussels {{Dreissena}} Polymorpha ({{Pallas}}, 1771) at the Forefront of an Invasion Event in {{Lake Winnipeg}}, {{Manitoba}}, {{Canada}}},
  author = {Gingera, Timothy and Bajno, Robert and Docker, Margaret and Reist, James},
  year = {2017},
  journal = {Management of Biological Invasions},
  volume = {8},
  number = {3},
  pages = {287--300},
  issn = {19898649},
  doi = {10.3391/mbi.2017.8.3.03},
  url = {http://www.reabic.net/journals/mbi/2017/Issue3.aspx},
  urldate = {2024-11-01},
  abstract = {Zebra mussel Dreissena polymorpha (Pallas, 1771), an invasive bivalve in North America, was first recorded in Lake Winnipeg in 2013. Quagga mussel D. bugensis (Andrusov, 1897), a second invasive mussel to North America, has yet to be detected in Manitoba waters. The establishment and continued spread of zebra mussels is of concern. Accordingly, we developed two species-specific and one Dreissena-specific qPCR-based environmental DNA (eDNA) assays designed as a single multiplexed reaction able to identify the presence of zebra mussel and infer the presence of quagga mussel in water samples from at-risk and invaded locations. In 2014, samples were collected from four invaded harbours on Lake Winnipeg during the early (May) and late (October) seasons. Zebra mussel eDNA was detected in 0--33.3\% of samples per site early in the season, whereas late season sample detection ranged from 42.9 to 100\%. In order to confirm that there was no nonspecific amplification of DNA from local biota, samples were also collected from sites where neither adult nor veliger-stage zebra mussels had been previously observed. These sites included three harbours on Lake Winnipeg and six sites within the Manitoban portion of the Red River. No amplification of eDNA was recorded at these sites except for that at a float-plane dock in the Red River upstream of Lake Winnipeg. Zebra mussels were subsequently detected at this location by sampling for transformed attached individuals. Thus, we demonstrate that eDNA is an early indicator of the presence of zebra mussels and is a useful detection tool at the forefront of their recent invasion in Manitoba. This work provides the foundation for the development of a zebra mussel eDNA monitoring program for waterbodies in Manitoba and western Canada.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/EJWWX85R/Gingera et al. - 2017 - Environmental DNA as a detection tool for zebra mu.pdf}
}

