@article{goldberg_etal2011MolecularDetection,
  title = {Molecular {{Detection}} of {{Vertebrates}} in {{Stream Water}}: {{A Demonstration Using Rocky Mountain Tailed Frogs}} and {{Idaho Giant Salamanders}}},
  shorttitle = {Molecular {{Detection}} of {{Vertebrates}} in {{Stream Water}}},
  author = {Goldberg, Caren S. and Pilliod, David S. and Arkle, Robert S. and Waits, Lisette P.},
  editor = {Gratwicke, Brian},
  year = {2011},
  month = jul,
  journal = {PLoS ONE},
  volume = {6},
  number = {7},
  pages = {e22746},
  issn = {1932-6203},
  doi = {10.1371/journal.pone.0022746},
  url = {https://dx.plos.org/10.1371/journal.pone.0022746},
  urldate = {2024-11-01},
  abstract = {Stream ecosystems harbor many secretive and imperiled species, and studies of vertebrates in these systems face the challenges of relatively low detection rates and high costs. Environmental DNA (eDNA) has recently been confirmed as a sensitive and efficient tool for documenting aquatic vertebrates in wetlands and in a large river and canal system. However, it was unclear whether this tool could be used to detect low-density vertebrates in fast-moving streams where shed cells may travel rapidly away from their source. To evaluate the potential utility of eDNA techniques in stream systems, we designed targeted primers to amplify a short, species-specific DNA fragment for two secretive stream amphibian species in the northwestern region of the United States (Rocky Mountain tailed frogs, Ascaphus montanus, and Idaho giant salamanders, Dicamptodon aterrimus). We tested three DNA extraction and five PCR protocols to determine whether we could detect eDNA of these species in filtered water samples from five streams with varying densities of these species in central Idaho, USA. We successfully amplified and sequenced the targeted DNA regions for both species from stream water filter samples. We detected Idaho giant salamanders in all samples and Rocky Mountain tailed frogs in four of five streams and found some indication that these species are more difficult to detect using eDNA in early spring than in early fall. While the sensitivity of this method across taxa remains to be determined, the use of eDNA could revolutionize surveys for rare and invasive stream species. With this study, the utility of eDNA techniques for detecting aquatic vertebrates has been demonstrated across the majority of freshwater systems, setting the stage for an innovative transformation in approaches for aquatic research.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/KTZ4BRRM/Goldberg et al. - 2011 - Molecular Detection of Vertebrates in Stream Water.pdf}
}

@article{goldberg_etal2013EnvironmentalDNA,
  title = {Environmental {{DNA}} as a New Method for Early Detection of {{New Zealand}} Mudsnails ( {{{\emph{Potamopyrgus}}}}{\emph{ Antipodarum}} )},
  author = {Goldberg, Caren S. and Sepulveda, Adam and Ray, Andrew and Baumgardt, Jeremy and Waits, Lisette P.},
  year = {2013},
  month = sep,
  journal = {Freshwater Science},
  volume = {32},
  number = {3},
  pages = {792--800},
  issn = {2161-9549, 2161-9565},
  doi = {10.1899/13-046.1},
  url = {https://www.journals.uchicago.edu/doi/10.1899/13-046.1},
  urldate = {2024-11-01},
  abstract = {Early detection of aquatic invasive species is a critical task for management of aquatic ecosystems. This task is hindered by the difficulty and cost of surveying aquatic systems thoroughly. The New Zealand mudsnail (Potamopyrgus antipodarum) is a small, invasive parthenogenic mollusk that can reach very high population densities and severely affects ecosystem functioning. To assist in the early detection of this invasive species, we developed and validated a highly sensitive environmental deoxyribonucleic acid (eDNA) assay. We used a dose--response laboratory experiment to investigate the relationship between New Zealand mudsnail density and eDNA detected through time. We documented that as few as 1 individual in 1.5 L of water for 2 d could be detected with this method, and that eDNA from this species may remain detectable for 21 to 44 d after mudsnail removal. We used the eDNA method to confirm the presence of New Zealand mudsnail eDNA at densities as low as 11 to 144 snails/m2 in a eutrophic 5th-order river. Combined, these results demonstrate the high potential for eDNA surveys to assist with early detection of a widely distributed invasive aquatic invertebrate.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/SIHZVDNA/Goldberg et al. - 2013 - Environmental DNA as a new method for early detect.pdf}
}

