@article{goldberg_etal2015Movingenvironmental,
  title = {Moving Environmental {{DNA}} Methods from Concept to Practice for Monitoring Aquatic Macroorganisms},
  author = {Goldberg, Caren S. and Strickler, Katherine M. and Pilliod, David S.},
  year = {2015},
  month = mar,
  journal = {Biological Conservation},
  volume = {183},
  pages = {1--3},
  issn = {00063207},
  doi = {10.1016/j.biocon.2014.11.040},
  url = {https://linkinghub.elsevier.com/retrieve/pii/S0006320714004650},
  urldate = {2024-11-01},
  abstract = {The discovery that macroorganisms can be detected from their environmental DNA (eDNA) in aquatic systems has immense potential for the conservation of biological diversity. This special issue contains 11 papers that review and advance the field of eDNA detection of vertebrates and other macroorganisms, including studies of eDNA production, transport, and degradation; sample collection and processing to maximize detection rates; and applications of eDNA for conservation using citizen scientists. This body of work is an important contribution to the ongoing efforts to take eDNA detection of macroorganisms from technical breakthrough to established, reliable method that can be used in survey, monitoring, and research applications worldwide. While the rapid advances in this field are remarkable, important challenges remain, including consensus on best practices for collection and analysis, understanding of eDNA diffusion and transport, and avoidance of inhibition in sample collection and processing. Nonetheless, as demonstrated in this special issue, eDNA techniques for research and monitoring are beginning to realize their potential for contributing to the conservation of biodiversity globally.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/628QHGMY/Goldberg et al. - 2015 - Moving environmental DNA methods from concept to p.pdf}
}

@article{goldberg_etal2016Criticalconsiderations,
  title = {Critical Considerations for the Application of Environmental {{{\textsc{DNA}}}} Methods to Detect Aquatic Species},
  shorttitle = {Critical Considerations for the Application of Environmental},
  author = {Goldberg, Caren S. and Turner, Cameron R. and Deiner, Kristy and Klymus, Katy E. and Thomsen, Philip Francis and Murphy, Melanie A. and Spear, Stephen F. and McKee, Anna and Oyler-McCance, Sara J. and Cornman, Robert Scott and Laramie, Matthew B. and Mahon, Andrew R. and Lance, Richard F. and Pilliod, David S. and Strickler, Katherine M. and Waits, Lisette P. and Fremier, Alexander K. and Takahara, Teruhiko and Herder, Jelger E. and Taberlet, Pierre},
  editor = {Gilbert, M.},
  year = {2016},
  month = nov,
  journal = {Methods in Ecology and Evolution},
  volume = {7},
  number = {11},
  pages = {1299--1307},
  issn = {2041-210X, 2041-210X},
  doi = {10.1111/2041-210X.12595},
  url = {https://besjournals.onlinelibrary.wiley.com/doi/10.1111/2041-210X.12595},
  urldate = {2024-11-01},
  abstract = {Summary                                                                                     Species detection using environmental                     DNA                     (                     eDNA                     ) has tremendous potential for contributing to the understanding of the ecology and conservation of aquatic species. Detecting species using                     eDNA                     methods, rather than directly sampling the organisms, can reduce impacts on sensitive species and increase the power of field surveys for rare and elusive species. The sensitivity of                     eDNA                     methods, however, requires a heightened awareness and attention to quality assurance and quality control protocols. Additionally, the interpretation of                     eDNA                     data demands careful consideration of multiple factors. As                     eDNA                     methods have grown in application, diverse approaches have been implemented to address these issues. With interest in                     eDNA                     continuing to expand, supportive guidelines for undertaking                     eDNA                     studies are greatly needed.                                                                                             Environmental                     DNA                     researchers from around the world have collaborated to produce this set of guidelines and considerations for implementing                     eDNA                     methods to detect aquatic macroorganisms.                                                                                             Critical considerations for study design include preventing contamination in the field and the laboratory, choosing appropriate sample analysis methods, validating assays, testing for sample inhibition and following minimum reporting guidelines. Critical considerations for inference include temporal and spatial processes, limits of correlation of                     eDNA                     with abundance, uncertainty of positive and negative results, and potential sources of allochthonous                     DNA                     .                                                                        We present a synthesis of knowledge at this stage for application of this new and powerful detection method.},
  copyright = {http://creativecommons.org/licenses/by-nc-nd/4.0/},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/2RCUIK2N/Goldberg et al. - 2016 - Critical considerations for the application of env.pdf}
}

@article{goldberg_etal2018Degradationdispersion,
  title = {Degradation and Dispersion Limit Environmental {{DNA}} Detection of Rare Amphibians in Wetlands: {{Increasing}} Efficacy of Sampling Designs},
  shorttitle = {Degradation and Dispersion Limit Environmental {{DNA}} Detection of Rare Amphibians in Wetlands},
  author = {Goldberg, Caren S. and Strickler, Katherine M. and Fremier, Alexander K.},
  year = {2018},
  month = aug,
  journal = {Science of The Total Environment},
  volume = {633},
  pages = {695--703},
  issn = {00489697},
  doi = {10.1016/j.scitotenv.2018.02.295},
  url = {https://linkinghub.elsevier.com/retrieve/pii/S0048969718306958},
  urldate = {2024-11-01},
  abstract = {The detection of rare macroorganisms using environmental DNA (eDNA) is a powerful new method for conservation and management; the efficacy of this method is affected by physiological, ecological, and hydrological processes. Understanding the processes limiting eDNA detection and accounting for those factors with optimized sampling designs is critical for realizing the potential of this tool. Amphibians are a focus of conservation programs globally and are often difficult to detect, presenting a challenge for effective action. To increase the ability of eDNA techniques to inform conservation and management programs, we investigated the eDNA detection of amphibians compared with field surveys for six species across a gradient of environmental factors expected to affect eDNA detection in three different systems: perennial wetlands, intermittent wetlands, and acidic intermittent wetlands. We applied a baseline sampling design in each wetland and used an occupancy modeling approach to evaluate evidence for processes limiting detection for each species given the presence of the target species. Evidence weights indicated that limiting processes varied across systems and included those associated with increased degradation (pH b 5, temperature N 25 {$^\circ$}C) and limited dispersion (wetland area N 1200 m2, sample volume b 200 mL). Optimized sampling protocols based on model results included an increased number of sampling locations in large and highly degradative (acidic) wetlands and increased filter pore size in highparticulate systems. These improved designs compensated for the previously limiting factors and yielded average detection rates of 0.62--0.86 per water sample. Degradation and dispersion processes appear to strongly influence the detection of amphibians in wetlands. Optimized, adaptive sampling designs can greatly increase the efficacy of eDNA monitoring approaches.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/6RUIVYYR/Goldberg et al. - 2018 - Degradation and dispersion limit environmental DNA.pdf}
}

