@article{hall_etal2016Evaluatingenvironmental,
  title = {Evaluating Environmental {{{\textsc{DNA}}}} -based Quantification of Ranavirus Infection in Wood Frog Populations},
  shorttitle = {Evaluating Environmental},
  author = {Hall, Emily M. and Crespi, Erica J. and Goldberg, Caren S. and Brunner, Jesse L.},
  year = {2016},
  month = mar,
  journal = {Molecular Ecology Resources},
  volume = {16},
  number = {2},
  pages = {423--433},
  issn = {1755-098X, 1755-0998},
  doi = {10.1111/1755-0998.12461},
  url = {https://onlinelibrary.wiley.com/doi/10.1111/1755-0998.12461},
  urldate = {2024-11-01},
  abstract = {A variety of challenges arise when monitoring wildlife populations for disease. Sampling tissues can be invasive to hosts, and obtaining sufficient sample sizes can be expensive and time consuming, particularly for rare species and when pathogen prevalence is low. Environmental DNA (eDNA)-based detection of pathogens is an alternative approach to surveillance for aquatic communities that circumvents many of these issues. Ranaviruses are emerging pathogens of ectothermic vertebrates linked to die-offs of amphibian populations. Detecting ranavirus infections is critical, but non-lethal methods have the above issues and are prone to false negatives. We report on the feasibility and effectiveness of eDNA-based ranavirus detection in the field. We compared ranavirus titers in eDNA samples collected from pond water to titers in wood frog (Lithobates sylvaticus; n=5) tadpoles in sites dominated by this one species (n= 20 pond visits). We examined whether ranavirus DNA can be detected in eDNA from pond water when infections are present in the pond and if viral titers detected in eDNA samples correlate with the prevalence or intensity of ranavirus infections in tadpoles. With three 250 mL water samples we were able to detect the virus in all visits with infected larvae (0.92 diagnostic sensitivity). Also, we found a strong relationship between the viral eDNA titers and titers in larval tissues. eDNA titers increased prior to observed die-offs and declined afterwards, and were 2 orders of magnitude higher in ponds with a die-off. Our results suggest that eDNA is useful for detecting ranavirus infections in wildlife and aquaculture.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/X2GY6MD2/Hall et al. - 2016 - Evaluating environmental span style=font-variant.pdf}
}

