@article{hardiman_etal2004PredatorsInfluence,
  title = {Do {{Predators Influence}} the {{Distribution}} of {{Age-0 Kokanee}} in a {{Colorado Reservoir}}?},
  author = {Hardiman, Jill M. and Johnson, Brett M. and Martinez, Patrick J.},
  year = {2004},
  month = nov,
  journal = {Transactions of the American Fisheries Society},
  volume = {133},
  number = {6},
  pages = {1366--1378},
  issn = {0002-8487, 1548-8659},
  doi = {10.1577/T03-1234.1},
  url = {http://doi.wiley.com/10.1577/T03-1234.1},
  urldate = {2024-05-10},
  abstract = {Seasonal changes in reservoir conditions such as productivity, light, and temperature create spatiotemporal variation in habitat that may segregate or aggregate predators and prey, producing implications for the distribution, growth, and survival of fishes. We used hydroacoustics to document the diel vertical distribution of age-0 kokanee Oncorhynchus nerka relative to environmental gradients at Blue Mesa Reservoir, Colorado, during May--August of 2002. Temperature, light, and zooplankton density profiles were examined relative to foraging conditions for kokanee and their primary predator, lake trout Salvelinus namaycush. Age-0 kokanee displayed large diel vertical migrations in May despite the lack of an energetic advantage before reservoir stratification. Age-0 kokanee minimized near-surface foraging at this time, perhaps to avoid predation by visual predators, such as lake trout, in the well-lit surface waters. Strong reservoir stratification in midsummer appeared to provide a thermal refuge from lake trout that the kokanee exploited. By August vertical migrations were shallow and most kokanee remained in the epilimnion throughout the day. Although the energetic implications of the late-summer strategy are unclear, it appears that kokanee were responding to changes in their predator environment. A robust model for kokanee diel vertical migration across a range of systems should include a predator avoidance component.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/YTZXKK2S/Hardiman et al. - 2004 - Do Predators Influence the Distribution of Age-0 K.pdf}
}

