@article{hossack_etal2006Divergentpatterns,
  title = {Divergent Patterns of Abundance and Age-Class Structure of Headwater Stream Tadpoles in Burned and Unburned Watersheds},
  author = {Hossack, Blake R. and Corn, Paul Stephen and Fagre, Daniel B.},
  year = {2006},
  month = oct,
  journal = {Canadian Journal of Zoology},
  volume = {84},
  number = {10},
  pages = {1482--1488},
  issn = {0008-4301, 1480-3283},
  doi = {10.1139/z06-143},
  url = {http://www.nrcresearchpress.com/doi/10.1139/z06-143},
  urldate = {2024-11-01},
  abstract = {Wildfire is a potential threat to many species with narrow environmental tolerances like the Rocky Mountain tailed frog (Ascaphus montanus Mittleman and Myers, 1949), which inhabits a region where the frequency and intensity of wildfires are expected to increase. We compared pre- and post-fire counts of tadpoles in eight streams in northwestern Montana to determine the effects of wildfire on A. montanus. All streams were initially sampled in 2001, 2 years before four of them burned in a large wildfire, and were resampled during the 2 years following the fire. Counts of tadpoles were similar in the two groups of streams before the fire. After the fire, tadpoles were almost twice as abundant in unburned streams than in burned streams. The fire seemed to have the greatest negative effect on abundance of age-1 tadpoles, which was reflected in the greater variation in same-stream age-class structure compared with those in unburned streams. Despite the apparent effect on tadpoles, we do not expect the wildfire to be an extirpation threat to populations in the streams that we sampled. Studies spanning a chronosequence of fires, as well as in other areas, are needed to assess the effects of fires on streams with A. montanus and to determine the severity and persistence of these effects. Re{\textasciiacute}sume{\textasciiacute} : Les feux de brousse sont une menace potentielle a` de nombreuses espe`ces a` tole{\textasciiacute}rance environnementale e{\textasciiacute}troite et en particulier a` la grenouille a` queue des Rocheuses (Ascaphus montanus Mittleman et Myers, 1949) qui habite une re{\textasciiacute}gion ou` les feux de brousse risquent d'augmenter en fre{\textasciiacute}quence et en intensite{\textasciiacute} a` l'avenir. Nous avons compare{\textasciiacute} les de{\textasciiacute}nombrements de te{\textasciicircum}tards avant et apre`s un feu dans huit cours d'eau du nord-ouest du Montana afin de de{\textasciiacute}terminer les effets des feux de brousse sur A. montanus. Tous les cours d'eau ont e{\textasciiacute}te{\textasciiacute} e{\textasciiacute}chantillonne{\textasciiacute}s en 2001, 2 ans avant que quatre d'entre eux aient e{\textasciiacute}te{\textasciiacute} affecte{\textasciiacute}s par un important feu de brousse, et ils ont tous e{\textasciiacute}te{\textasciiacute} e{\textasciiacute}chantillonne{\textasciiacute}s a` nouveau 2 ans apre`s le feu. Les de{\textasciiacute}nombrements de te{\textasciicircum}tards e{\textasciiacute}taient semblables dans les deux groupes de cours d'eau avant le feu. Apre`s le feu, les te{\textasciicircum}tards e{\textasciiacute}taient presque deux fois plus nombreux dans les cours d'eau non affecte{\textasciiacute}s par le feu que dans les cours d'eau affecte{\textasciiacute}s. Malgre{\textasciiacute} cet effet apparent sur les te{\textasciicircum}tards, nous ne comptons pas que les feux de brousse constituent une menace d'e{\textasciiacute}limination des populations dans les cours d'eau que nous avons e{\textasciiacute}chantillonne{\textasciiacute}s. Il faudrait mener des e{\textasciiacute}tudes couvrant une chronose{\textasciiacute}quence de feux et faire des recherches dans d'autres re{\textasciiacute}gions afin d'e{\textasciiacute}valuer les effets sur les cours d'eau a` A. montanus et pour de{\textasciiacute}terminer la se{\textasciiacute}ve{\textasciiacute}rite{\textasciiacute} et la persistance des effets.},
  copyright = {http://www.nrcresearchpress.com/page/about/CorporateTextAndDataMining},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/TYWWHAIH/Hossack et al. - 2006 - Divergent patterns of abundance and age-class stru.pdf}
}

