@article{hwan_etal2018Dryseason,
  title = {Dry Season Survival of Juvenile Salmonids in an Intermittent Coastal Stream},
  author = {Hwan, Jason L. and {Fern{\'a}ndez-Chac{\'o}n}, Albert and Buoro, Mathieu and Carlson, Stephanie M.},
  year = {2018},
  month = may,
  journal = {Canadian Journal of Fisheries and Aquatic Sciences},
  volume = {75},
  number = {5},
  pages = {746--758},
  issn = {0706-652X, 1205-7533},
  doi = {10.1139/cjfas-2017-0026},
  url = {http://www.nrcresearchpress.com/doi/10.1139/cjfas-2017-0026},
  urldate = {2023-03-11},
  abstract = {We estimated dry season survival of imperiled salmonids in an intermittent coastal stream in California across 4 years (2009--2012). Our study encompassed two dry and two wet winters, allowing us to explore patterns of survival across and within dry seasons with different antecedent precipitation. Apparent survival of age-0+ steelhead trout (Oncorhynchus mykiss) was higher following wet winters compared with dry winters. Moreover, antecedent precipitation was positively correlated with cumulative survival of age-0+ steelhead. Within years, apparent survival of steelhead varied among weeks with a tendency to decrease in the late summer, indicating that fish exhibited some resistance to seasonal drought. Additionally, we found a slight but significant survival advantage for age-0+ coho salmon (Oncorhynchus kisutch) compared with equal-aged steelhead. Our results emphasize the influence of antecedent precipitation in driving the survival of imperiled salmonids and highlight that these fishes are somewhat resistant to seasonal drought, at least to a point.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/5PGEQ8QX/hwan_et_al_2018_dry_season_survival_of_juvenile_salmonids_in_an_intermittent_coastal_stream.pdf}
}

@article{iacarella_weller2024Predictingfavourable,
  title = {Predicting Favourable Streams for Anadromous Salmon Spawning and Natal Rearing under Climate Change},
  author = {Iacarella, Josephine C. and Weller, J. Daniel},
  year = {2024},
  month = jan,
  journal = {Canadian Journal of Fisheries and Aquatic Sciences},
  volume = {81},
  number = {1},
  pages = {1--13},
  publisher = {NRC Research Press},
  issn = {0706-652X},
  doi = {10.1139/cjfas-2023-0096},
  url = {https://cdnsciencepub.com/doi/full/10.1139/cjfas-2023-0096},
  urldate = {2024-06-25},
  abstract = {Successful management of imperilled anadromous salmon stocks requires understanding how salmon will respond to a changing climate across their extensive ranges. Though largely unused for anadromous salmon to date, environmental niche models (ENMs) can provide a strong foundation for estimating where and how much habitat will likely remain favourable. We applied a comprehensive suite of ENM statistical approaches to five Pacific salmon species, focusing on freshwater spawning and natal rearing habitat as not only critical for population survival, but also highly susceptible to a changing climate. We developed ENMs for spawning ranges across British Columbia (BC), Canada, and western US, and projected future scenarios for BC. Projections indicated net expansion of favourable spawning habitat across species in BC, with the most change occurring by 2060. However, shifts in favourable habitat varied greatly geographically and by species, with notable reductions in coastal and southern interior streams. Our results provide a basis for identifying important changes in spawning and natal rearing habitat conditions to help inform priorities for restoration and protection actions.},
  file = {/Users/airvine/Zotero/storage/SDQ3D9XR/Iacarella and Weller - 2024 - Predicting favourable streams for anadromous salmo.pdf}
}

