@article{isaak_etal2017NorWeSTSummer,
  title = {The {{NorWeST Summer Stream Temperature Model}} and {{Scenarios}} for the {{Western U}}.{{S}}.: {{A Crowd-Sourced Database}} and {{New Geospatial Tools Foster}} a {{User Community}} and {{Predict Broad Climate Warming}} of {{Rivers}} and {{Streams}}},
  shorttitle = {The {{NorWeST Summer Stream Temperature Model}} and {{Scenarios}} for the {{Western U}}.{{S}}.},
  author = {Isaak, Daniel J. and Wenger, Seth J. and Peterson, Erin E. and Ver Hoef, Jay M. and Nagel, David E. and Luce, Charles H. and Hostetler, Steven W. and Dunham, Jason B. and Roper, Brett B. and Wollrab, Sherry P. and Chandler, Gwynne L. and Horan, Dona L. and {Parkes-Payne}, Sharon},
  year = {2017},
  journal = {Water Resources Research},
  volume = {53},
  number = {11},
  pages = {9181--9205},
  issn = {1944-7973},
  doi = {10.1002/2017WR020969},
  url = {https://onlinelibrary.wiley.com/doi/abs/10.1002/2017WR020969},
  urldate = {2024-06-25},
  abstract = {Thermal regimes are fundamental determinants of aquatic ecosystems, which makes description and prediction of temperatures critical during a period of rapid global change. The advent of inexpensive temperature sensors dramatically increased monitoring in recent decades, and although most monitoring is done by individuals for agency-specific purposes, collectively these efforts constitute a massive distributed sensing array that generates an untapped wealth of data. Using the framework provided by the National Hydrography Dataset, we organized temperature records from dozens of agencies in the western U.S. to create the NorWeST database that hosts {$>$}220,000,000 temperature recordings from {$>$}22,700 stream and river sites. Spatial-stream-network models were fit to a subset of those data that described mean August water temperatures (AugTw) during 63,641 monitoring site-years to develop accurate temperature models (r2 = 0.91; RMSPE = 1.10{$^\circ$}C; MAPE = 0.72{$^\circ$}C), assess covariate effects, and make predictions at 1 km intervals to create summer climate scenarios. AugTw averaged 14.2{$^\circ$}C (SD = 4.0{$^\circ$}C) during the baseline period of 1993--2011 in 343,000 km of western perennial streams but trend reconstructions also indicated warming had occurred at the rate of 0.17{$^\circ$}C/decade (SD = 0.067{$^\circ$}C/decade) during the 40 year period of 1976--2015. Future scenarios suggest continued warming, although variation will occur within and among river networks due to differences in local climate forcing and stream responsiveness. NorWeST scenarios and data are available online in user-friendly digital formats and are widely used to coordinate monitoring efforts among agencies, for new research, and for conservation planning.},
  langid = {english},
  keywords = {big data,climate change,climate scenarios,river network,sensor,stream temperature},
  file = {/Users/airvine/Zotero/storage/2MYBKAEB/Isaak et al. - 2017 - The NorWeST Summer Stream Temperature Model and Sc.pdf;/Users/airvine/Zotero/storage/P7WHIB7N/2017WR020969.html}
}

