  title = {Reliable, Verifiable and Efficient Monitoring of Biodiversity via Metabarcoding},
  author = {Ji, Yinqiu and Ashton, Louise and Pedley, Scott M. and Edwards, David P. and Tang, Yong and Nakamura, Akihiro and Kitching, Roger and Dolman, Paul M. and Woodcock, Paul and Edwards, Felicity A. and Larsen, Trond H. and Hsu, Wayne W. and Benedick, Suzan and Hamer, Keith C. and Wilcove, David S. and Bruce, Catharine and Wang, Xiaoyang and Levi, Taal and Lott, Martin and Emerson, Brent C. and Yu, Douglas W.},
