@article{langlois_etal2021needrobust,
  title = {The Need for Robust {{qPCR}}-based {{eDNA}} Detection Assays in Environmental Monitoring and Species Inventories},
  author = {Langlois, Valerie S. and Allison, Michael J. and Bergman, Lauren C. and To, Tuan Anh and Helbing, Caren C.},
  year = {2021},
  month = may,
  journal = {Environmental DNA},
  volume = {3},
  number = {3},
  pages = {519--527},
  issn = {2637-4943, 2637-4943},
  doi = {10.1002/edn3.164},
  url = {https://onlinelibrary.wiley.com/doi/10.1002/edn3.164},
  urldate = {2024-11-01},
  abstract = {Considerable promise and excitement exist in the application of environmental DNA (eDNA) methods to environmental monitoring and species inventories as eDNA can provide cost-effective and accurate biodiversity information. However, considerable variation in data quality, rigor, and reliability has eroded confidence in eDNA application and is limiting regulatory and policy uptake. Substantial effort has gone into promoting transparency in reporting and deriving standardized frameworks and methods for eDNA field workflow components, but surprisingly little scrutiny has been given to the design and performance elements of targeted eDNA detection assays which, by far, have been most used in the scientific literature. There are several methods used for eDNA detection. The most accessible, cost-effective, and conducive to standards development is targeted real-time or quantitative real-time polymerase chain reaction (abbreviated as qPCR) eDNA analysis. The present perspective is meant to assist in the development and evaluation of qPCR-based eDNA assays. It evaluates six steps in the qPCR-based eDNA assay development and validation workflow identifying and addressing concerns pertaining to poor qPCR assay design and implementation; identifies the need for more fulsome mitochondrial genome sequence information for a broader range of species; and brings solutions toward best practices in forthcoming large-scale and worldwide eDNA applications, such as atrisk or invasive species assessments and site remediation monitoring.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/RANK7IHT/Langlois et al. - 2021 - The need for robust qPCR‐based eDNA detection assa.pdf}
}

@article{laramie_etal2015Characterizingdistribution,
  title = {Characterizing the Distribution of an Endangered Salmonid Using Environmental {{DNA}} Analysis},
  author = {Laramie, Matthew B. and Pilliod, David S. and Goldberg, Caren S.},
  year = {2015},
  month = mar,
  journal = {Biological Conservation},
  volume = {183},
  pages = {29--37},
  issn = {00063207},
  doi = {10.1016/j.biocon.2014.11.025},
  url = {https://linkinghub.elsevier.com/retrieve/pii/S0006320714004509},
  urldate = {2024-11-01},
  abstract = {Determining species distributions accurately is crucial to developing conservation and management strategies for imperiled species, but a challenging task for small populations. We evaluated the efficacy of environmental DNA (eDNA) analysis for improving detection and thus potentially refining the known distribution of Chinook salmon (Oncorhynchus tshawytscha) in the Methow and Okanogan Subbasins of the Upper Columbia River, which span the border between Washington, USA and British Columbia, Canada. We developed an assay to target a 90 base pair sequence of Chinook DNA and used quantitative polymerase chain reaction (qPCR) to quantify the amount of Chinook eDNA in triplicate 1-L water samples collected at 48 stream locations in June and again in August 2012. The overall probability of detecting Chinook with our eDNA method in areas within the known distribution was 0.77 ({\textpm}0.05 SE). Detection probability was lower in June (0.62, {\textpm}0.08 SE) during high flows and at the beginning of spring Chinook migration than during base flows in August (0.93, {\textpm}0.04 SE). In the Methow subbasin, mean eDNA concentration was higher in August compared to June, especially in smaller tributaries, probably resulting from the arrival of spring Chinook adults, reduced discharge, or both. Chinook eDNA concentrations did not appear to change in the Okanogan subbasin from June to August. Contrary to our expectations about downstream eDNA accumulation, Chinook eDNA did not decrease in concentration in upstream reaches (0--120 km). Further examination of factors influencing spatial distribution of eDNA in lotic systems may allow for greater inference of local population densities along stream networks or watersheds. These results demonstrate the potential effectiveness of eDNA detection methods for determining landscape-level distribution of anadromous salmonids in large river systems.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/JDUNC2DZ/Laramie et al. - 2015 - Characterizing the distribution of an endangered s.pdf}
}

