  doi = {10.1002/esp.4238},
  url = {https://onlinelibrary.wiley.com/doi/10.1002/esp.4238},
  urldate = {2024-11-01},
  abstract = {Geomorphology has increasingly considered the role of biotic factors as controls upon geomorphic processes across a wide range of spatial and temporal scales. Where timescales are long (centennial and longer), it has been possible to quantify relationships between geomorphic processes and vegetation using, for example, the pollen record. However, where the biotic agents are fauna, longer term reconstruction of the impacts of biological activity upon geomorphic processes is more challenging. Here, we review the prospect of using environmental DNA as a molecular proxy to decipher the presence and nature of faunal influences on geomorphic processes in both present and ancient deposits. When used appropriately, this method has the potential to improve our understanding of biotic drivers of geomorphic processes, notably fauna, over long timescales and so to reconstruct how such drivers might explain the landscape as we see it today. Copyright {\copyright} 2017 John Wiley \& Sons, Ltd.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/2GJ25ZJB/Larsen et al. - 2018 - Biotic drivers of river and floodplain geomorpholo.pdf}
}

