@article{loeza-quintana_etal2021EnvironmentalDNA,
  title = {Environmental {{DNA}} Detection of Endangered and Invasive Species in {{Kejimkujik National Park}} and {{Historic Site}}},
  author = {{Loeza-Quintana}, Tzitziki and Crookes, Steven and Li, Pei Yuan and Reid, Darrin P. and Smith, Matthew and Hanner, Robert H.},
  year = {2021},
  month = mar,
  journal = {Genome},
  volume = {64},
  number = {3},
  pages = {172--180},
  issn = {0831-2796, 1480-3321},
  doi = {10.1139/gen-2020-0042},
  url = {https://cdnsciencepub.com/doi/10.1139/gen-2020-0042},
  urldate = {2024-11-01},
  abstract = {The use of environmental DNA (eDNA) allows the early detection of aquatic species at low densities (e.g., elusive and invasive species), which otherwise could be challenging to monitor using conventional techniques. Here, we assess the ability of eDNA sampling to detect the presence or absence of one species at risk (Blanding's turtle) and two invasive species (chain pickerel and smallmouth bass) in Kejimkujik National Park and National Historic Site, Nova Scotia, where the aquatic system is highly acidic and rich in organic compounds. Five replicates of 1 L water samples were taken per sampling site. Water filtration and eDNA extractions were performed on-site, while qPCR reactions were performed in the laboratory using species-specific assays. Samples were treated with an inhibition removal kit and analyzed pre- and post-inhibition removal. Despite the low pH and PCR inhibitors in water samples, our results showed positive eDNA detections in almost all expected positive sites (except in one site for Blanding's turtle). Detections of the target species were also observed at sites where their presence was previously unknown. Our study supports the advantage of eDNA to monitor species at low densities, revealing new distributions or recently invaded areas. We also demonstrate how eDNA can directly instruct management strategies in Kejimkujik.},
  copyright = {http://www.nrcresearchpress.com/page/about/CorporateTextAndDataMining},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/SK8DWGSU/Loeza-Quintana et al. - 2021 - Environmental DNA detection of endangered and inva.pdf}
}

