@article{millard-martin_etal2022EfficacyVisual,
  title = {Efficacy of {{Visual Encounter Surveys}} for {{Coastal Tailed Frog Detection}}},
  author = {{Millard-Martin}, Ben and Todd, Melissa and Johnson, Chris J. and McEwan, Alexandria L.},
  year = {2022},
  month = jun,
  journal = {Journal of Fish and Wildlife Management},
  volume = {13},
  number = {1},
  pages = {272--285},
  issn = {1944-687X},
  doi = {10.3996/JFWM-21-011},
  url = {https://meridian.allenpress.com/jfwm/article/13/1/272/476000/Efficacy-of-Visual-Encounter-Surveys-for-Coastal},
  urldate = {2024-11-01},
  abstract = {Coastal tailed frogs Ascaphus truei inhabit montane streams and forested habitats in the Coast and Cascade mountains from northern California to the Skeena River watershed in northwestern British Columbia. Terrestrial adults and juveniles of this cryptic biphasic species are difficult to survey as they are small, do not vocalize, and may be associated with woody ground structures or subsurface refugia at considerable distances from natal streams. We performed a comparative analysis of the detection rate of postmetamorphic coastal tailed frogs and ecological factors hypothesized to influence detection when conducting visual encounter and pitfall trap surveys. We conducted concurrent surveys in northwestern British Columbia at six sites over similar time periods using both techniques. The average detection rate of visual encounter surveys (mean {$\frac{1}{4}$} 0.249, SD {$\frac{1}{4}$} 0.702) was greater than that of pitfall sampling (mean {$\frac{1}{4}$} 0.138, SD {$\frac{1}{4}$} 0.773) when cool temperatures and high humidity favor aboveground movement during the daytime. Light-touch ground searches of refuge habitats likely enhanced detection during visual surveys. Although the average detection rate was less, pitfall traps provided 24-h sampling and were less affected by the experience of the surveyor and the occurrence of ground cover. In general, variation in seasonal behavior influenced detection regardless of method. The relatively higher cost and fixed nature of pitfall traps should be weighed against the ability to apply more cost-effective visual encounter surveys to a greater number of sites.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/FXXGZT5X/Millard-Martin et al. - 2022 - Efficacy of Visual Encounter Surveys for Coastal T.pdf}
}

