@article{orihel_etal2017Internalphosphorus,
  title = {Internal Phosphorus Loading in {{Canadian}} Fresh Waters: A Critical Review and Data Analysis},
  shorttitle = {Internal Phosphorus Loading in {{Canadian}} Fresh Waters},
  author = {Orihel, Diane M. and Baulch, Helen M. and Casson, Nora J. and North, Rebecca L. and Parsons, Chris T. and Seckar, Dalila C.M. and Venkiteswaran, Jason J.},
  year = {2017},
  month = dec,
