  issn = {0706-652X, 1205-7533},
  doi = {10.1139/cjfas-2016-0500},
  url = {http://www.nrcresearchpress.com/doi/10.1139/cjfas-2016-0500},
  urldate = {2024-11-06},
  abstract = {Many physical, chemical, and biological processes in freshwater ecosystems mobilize the nutrient phosphorus (P) from sediments, which in turn may contribute to the formation of harmful algal blooms. Here, we critically reviewed internal P loading in Canadian fresh waters to understand the geographic patterns and environmental drivers of this important process. From 43 publications, we consolidated 618 estimates of internal P loading from Canadian freshwater ponds, lakes, reservoirs, and coastal wetlands (n = 70). Expressed in terms of total P, short-term gross rates in sediment samples (L               gross               ) ranged from -27 to 54 mg{$\cdot$}m               -2               {$\cdot$}day               -1               (n = 461), while long-term net rates in whole ecosystems (L               net               ) ranged from -1694 to 10\,640 mg{$\cdot$}m               -2               {$\cdot$}year               -1               (n = 157). The main environmental drivers of this variation were oxygen, pH, geology, and trophic state. Internal P loading tended to be higher during the open-water season and most prominent in small prairie lakes. Priorities for future research on internal P loading should include resolving methodological problems, assessing the relative importance of different mechanisms, examining the influence of anthropogenic activities, and quantifying rates in understudied ecosystems.},
  copyright = {http://www.nrcresearchpress.com/page/about/CorporateTextAndDataMining},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/YQNT8MH9/orihel_et_al_2017_internal_phosphorus_loading_in_canadian_fresh_waters_-_a_critical_review_and.pdf}
}

