@article{phillis_etal2018Endangeredwinterrun,
  title = {Endangered Winter-Run {{Chinook}} Salmon Rely on Diverse Rearing Habitats in a Highly Altered Landscape},
  author = {Phillis, Corey C. and Sturrock, Anna M. and Johnson, Rachel C. and Weber, Peter K.},
  year = {2018},
  journal = {Biological Conservation},
  volume = {217},
  pages = {358--362},
  issn = {00063207},
  doi = {10.1016/j.biocon.2017.10.023},
  url = {https://linkinghub.elsevier.com/retrieve/pii/S0006320717306742},
  urldate = {2024-08-24},
  abstract = {Protecting habitats for imperiled species is central to conservation efforts. However, for migratory species, identifying juvenile habitats that confer success requires tracking individuals to reproduction. Here, we used otolith strontium isotope ratios (87Sr/86Sr) to reconstruct juvenile habitat use by endangered Sacramento River winter-run Chinook salmon that survived to adulthood. The isotope data revealed that 44--65\% of surviving adults reared in non-natal habitats, most of which is not designated as critical habitat under the Endangered Species Act. Juveniles entered these non-natal habitats at small sizes, yet left freshwater at a similar size to those that reared in the mainstem Sacramento River, suggesting these alternate rearing habitats provide suitable growth conditions. These findings indicate Sacramento River winter-run Chinook salmon rely on rearing habitats across a broader geographic region than previously known, potentially opening up greater restoration and conservation opportunities for species recovery.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/X5JAB47F/Phillis et al. - 2018 - Endangered winter-run Chinook salmon rely on diver.pdf}
}

