@article{pilliod_etal2013Estimatingoccupancy,
  title = {Estimating Occupancy and Abundance of Stream Amphibians Using Environmental {{DNA}} from Filtered Water Samples},
  author = {Pilliod, David S. and Goldberg, Caren S. and Arkle, Robert S. and Waits, Lisette P.},
  editor = {Richardson, John},
  year = {2013},
  month = aug,
  journal = {Canadian Journal of Fisheries and Aquatic Sciences},
  volume = {70},
  number = {8},
  pages = {1123--1130},
  issn = {0706-652X, 1205-7533},
  doi = {10.1139/cjfas-2013-0047},
  url = {http://www.nrcresearchpress.com/doi/10.1139/cjfas-2013-0047},
  urldate = {2024-11-01},
  abstract = {Environmental DNA (eDNA) methods for detecting aquatic species are advancing rapidly, but with little evaluation of field protocols or precision of resulting estimates. We compared sampling results from traditional field methods with eDNA methods for two amphibians in 13 streams in central Idaho, USA. We also evaluated three water collection protocols and the influence of sampling location, time of day, and distance from animals on eDNA concentration in the water. We found no difference in detection or amount of eDNA among water collection protocols. eDNA methods had slightly higher detection rates than traditional field methods, particularly when species occurred at low densities. eDNA concentration was positively related to field-measured density, biomass, and proportion of transects occupied. Precision of eDNA-based abundance estimates increased with the amount of eDNA in the water and the number of replicate subsamples collected. eDNA concentration did not vary significantly with sample location in the stream, time of day, or distance downstream from animals. Our results further advance the implementation of eDNA methods for monitoring aquatic vertebrates in stream habitats.},
  copyright = {http://www.nrcresearchpress.com/page/about/CorporateTextAndDataMining},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/LRE475JI/Pilliod et al. - 2013 - Estimating occupancy and abundance of stream amphi.pdf}
}

@article{pilliod_etal2014Factorsinfluencing,
  title = {Factors Influencing Detection of {{{\textsc{eDNA}}}} from a Stream-dwelling Amphibian},
  shorttitle = {Factors Influencing Detection Of},
  author = {Pilliod, David S. and Goldberg, Caren S. and Arkle, Robert S. and Waits, Lisette P.},
  year = {2014},
  month = jan,
  journal = {Molecular Ecology Resources},
  volume = {14},
  number = {1},
  pages = {109--116},
  issn = {1755-098X, 1755-0998},
  doi = {10.1111/1755-0998.12159},
  url = {https://onlinelibrary.wiley.com/doi/10.1111/1755-0998.12159},
  urldate = {2024-11-01},
  abstract = {Environmental DNA (eDNA) methods for detecting and estimating abundance of aquatic species are emerging rapidly, but little is known about how processes such as secretion rate, environmental degradation, and time since colonization or extirpation from a given site affect eDNA measurements. Using stream-dwelling salamanders and quantitative PCR (qPCR) analysis, we conducted three experiments to assess eDNA: (i) production rate; (ii) persistence time under different temperature and light conditions; and (iii) detectability and concentration through time following experimental introduction and removal of salamanders into previously unoccupied streams. We found that 44--50 g individuals held in aquaria produced 77 ng eDNA/h for 2 h, after which production either slowed considerably or began to equilibrate with degradation. eDNA in both full-sun and shaded treatments degraded exponentially to {$<$}1\% of the original concentration after 3 days. eDNA was no longer detectable in full-sun samples after 8 days, whereas eDNA was detected in 20\% of shaded samples after 11 days and 100\% of refrigerated control samples after 18 days. When translocated into unoccupied streams, salamanders were detectable after 6 h, but only when densities were relatively high (0.2481 individuals/m2) and when samples were collected within 5 m of the animals. Concentrations of eDNA detected were very low and increased steadily from 6--24 h after introduction, reaching 0.0022 ng/L. Within 1 h of removing salamanders from the stream, eDNA was no longer detectable. These results suggest that eDNA detectability and concentration depend on production rates of individuals, environmental conditions, density of animals, and their residence time.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/ERMJP3BC/Pilliod et al. - 2014 - Factors influencing detection of span style=font.pdf}
}

@article{piotrowski_napiorkowski2019Simplemodifications,
  title = {Simple Modifications of the Nonlinear Regression Stream Temperature Model for Daily Data},
  author = {Piotrowski, Adam P. and Napiorkowski, Jaroslaw J.},
  year = {2019},
  month = may,
  journal = {Journal of Hydrology},
  volume = {572},
  pages = {308--328},
  issn = {0022-1694},
  doi = {10.1016/j.jhydrol.2019.02.035},
  url = {https://www.sciencedirect.com/science/article/pii/S0022169419301921},
  urldate = {2024-10-26},
  abstract = {Among various stream temperature models those based on nonlinear regression frequently attract attention due to their simplicity and small number of required variables. Among such approaches the logistic regression model developed twenty years ago for weekly data is still widely used in various scientific studies that require quick and simple calculation of stream water temperature. The model has been modified a number of times in recent years to capture the relationship between daily stream water temperatures, air temperatures and discharge. In this study, we propose further modifications of the logistic regression model that do not require any additional variables that may be hard to measure. The proposed models capture the relationship between the stream temperature and the declination of the Sun, the air temperature and the discharge from a number of recent observations. The proposed approaches are tested on six rivers located in diverse orographic conditions of temperate climate zones of Europe and USA. Although the proposed models remain very simple, their performances are competitive against the performances of more advanced semi-physical or data-driven models.},
  keywords = {air2stream,Artificial neural networks,Logistic regression,Model calibration,Mohseni's model,Stream water temperature modelling}
}

