@article{pompanon_etal2012Whoeating,
  title = {Who Is Eating What: Diet Assessment Using next Generation Sequencing},
  shorttitle = {Who Is Eating What},
  author = {Pompanon, Francois and Deagle, Bruce E. and Symondson, William O. C. and Brown, David S. and Jarman, Simon N. and Taberlet, Pierre},
  year = {2012},
  month = apr,
  journal = {Molecular Ecology},
  volume = {21},
  number = {8},
  pages = {1931--1950},
  issn = {0962-1083, 1365-294X},
  doi = {10.1111/j.1365-294X.2011.05403.x},
  url = {https://onlinelibrary.wiley.com/doi/10.1111/j.1365-294X.2011.05403.x},
  urldate = {2024-11-01},
  abstract = {The analysis of food webs and their dynamics facilitates understanding of the mechanistic processes behind community ecology and ecosystem functions. Having accurate techniques for determining dietary ranges and components is critical for this endeavour. While visual analyses and early molecular approaches are highly labour intensive and often lack resolution, recent DNA-based approaches potentially provide more accurate methods for dietary studies. A suite of approaches have been used based on the identification of consumed species by characterization of DNA present in gut or faecal samples. In one approach, a standardized DNA region (DNA barcode) is PCR amplified, amplicons are sequenced and then compared to a reference database for identification. Initially, this involved sequencing clones from PCR products, and studies were limited in scale because of the costs and effort required. The recent development of next generation sequencing (NGS) has made this approach much more powerful, by allowing the direct characterization of dozens of samples with several thousand sequences per PCR product, and has the potential to reveal many consumed species simultaneously (DNA metabarcoding). Continual improvement of NGS technologies, on-going decreases in costs and current massive expansion of reference databases make this approach promising. Here we review the power and pitfalls of NGS diet methods. We present the critical factors to take into account when choosing or designing a suitable barcode. Then, we consider both technical and analytical aspects of NGS diet studies. Finally, we discuss the validation of data accuracy including the viability of producing quantitative data.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/WGALG7QX/Pompanon et al. - 2012 - Who is eating what diet assessment using next gen.pdf}
}

@article{porter_etal2000PredictiveModels,
  title = {Predictive {{Models}} of {{Fish Species Distribution}} in the {{Blackwater Drainage}}, {{British Columbia}}},
  author = {Porter, Marc S. and Rosenfeld, Jordan and Parkinson, Eric A.},
  year = {2000},
  month = may,
  journal = {North American Journal of Fisheries Management},
  volume = {20},
  number = {2},
  pages = {349--359},
  issn = {0275-5947, 1548-8675},
  doi = {10.1577/1548-8675(2000)020<0349:PMOFSD>2.3.CO;2},
  url = {http://doi.wiley.com/10.1577/1548-8675(2000)020<0349:PMOFSD>2.3.CO;2},
  urldate = {2021-10-19},
  abstract = {Management of fish biodiversity requires the ability to understand and predict expected species distributions. Models predicting species distributions can give insight into habitat requirements and expected probabilities of encountering species in unsampled areas, and help identify unique outlier populations and potential biodiversity hot spots. Previous research has shown that large-scale geomorphic variables can be linked to fish habitat use and used as predictors of fish species occurrence. Our goal was to determine whether reliable models of species distributions could be developed for freshwater fish in British Columbia, using large-scale macrohabitat data linkable to GIS (geographic information system) map coverages. We surveyed 48 streams in a representative watershed with high species diversity (the Blackwater) and developed statistical models based on macrohabitat variables to predict the distribution of 15 fish species found within the drainage. Correct classification rates of our logistic regression models based solely on map-based variables were generally high for most species found in the Blackwater (ranging from 73\% to 90\%) and showed only marginal improvement with inclusion of field-based information. The models correctly predicted the presence of individual species at stream sites 82.2\% of the time (on average) when applied to an independent data set from the Blackwater drainage, but were much less successful when applied to data from a geographically distant watershed (the Similkameen). Further refinements to quantify interregional variation in habitat conditions, however, should allow the development of regional fish distribution models as a GIS layer in British Columbia's new watershed atlas.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/QMY6FLX6/porter_et_al_2000_predictive_models_of_fish_species_distribution_in_the_blackwater_drainage,.pdf}
}

