@article{rees_etal2014applicationeDNA,
  title = {The Application of {{{\textsc{eDNA}}}} for Monitoring of the {{Great Crested Newt}} in the {{UK}}},
  shorttitle = {The Application Of},
  author = {Rees, Helen C. and Bishop, Keith and Middleditch, David J. and Patmore, James R. M. and Maddison, Ben C. and Gough, Kevin C.},
  year = {2014},
  month = nov,
  journal = {Ecology and Evolution},
  volume = {4},
  number = {21},
  pages = {4023--4032},
  issn = {2045-7758, 2045-7758},
  doi = {10.1002/ece3.1272},
  url = {https://onlinelibrary.wiley.com/doi/10.1002/ece3.1272},
  urldate = {2024-11-01},
  abstract = {Current ecological surveys for great crested newts are time-consuming and expensive and can only be carried out within a short survey window. Additional survey methods which would facilitate the detection of rare or protected species such as the great crested newt (Triturus cristatus) would be extremely advantageous. Environmental DNA (eDNA) analysis has been utilized for the detection of great crested newts in Denmark. Here, the same methodology has been applied to water samples taken from UK ponds concurrently with conventional field surveying techniques. Our eDNA analysis exhibited an 84\% success rate with a kappa coefficient of agreement between field and eDNA surveys of 0.86. One pond determined to be negative for great crested newt by field survey was positive by eDNA analysis, revealing the potential for improved detection rates using this methodology. Analysis of water samples collected in late summer indicates that eDNA analysis could be used to detect great crested newt after the optimal survey window for current field techniques had passed. Consequently, eDNA analysis could augment currently stipulated techniques for great crested newt surveying as a relatively quick and inexpensive tool for collecting great crested newt presence and distribution data within the UK instead of or prior to full field surveys.},
  copyright = {http://creativecommons.org/licenses/by/3.0/},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/SQDSNL3H/Rees et al. - 2014 - The application of span style=font-variantsmall.pdf}
}

@article{rees_etal2014REVIEWdetection,
  title = {{{REVIEW}}: {{The}} Detection of Aquatic Animal Species Using Environmental {{DNA}} -- a Review of {{eDNA}} as a Survey Tool in Ecology},
  shorttitle = {{{REVIEW}}},
  author = {Rees, Helen C. and Maddison, Ben C. and Middleditch, David J. and Patmore, James R.M. and Gough, Kevin C.},
  editor = {Crispo, Erika},
  year = {2014},
  month = oct,
  journal = {Journal of Applied Ecology},
  volume = {51},
  number = {5},
  pages = {1450--1459},
  issn = {0021-8901, 1365-2664},
  doi = {10.1111/1365-2664.12306},
  url = {https://besjournals.onlinelibrary.wiley.com/doi/10.1111/1365-2664.12306},
  urldate = {2024-11-01},
  abstract = {Summary                                                                Knowledge of species distribution is critical to ecological management and conservation biology. Effective management requires the detection of populations, which can sometimes be at low densities and is usually based on visual detection and counting.                                                                          Recently, there has been considerable interest in the detection of short species-specific environmental                     DNA                     (e                     DNA                     ) fragments to allow aquatic species monitoring within different environments due to the potential of greater sensitivity over traditional survey methods which can be time-consuming and costly.                                                                                             Environmental                     DNA                     analysis is increasingly being used in the detection of rare or invasive species and has also been applied to e                     DNA                     persistence studies and estimations of species biomass and distribution. When combined with next-generation sequencing methods, it has been demonstrated that entire faunas can be identified.                                                                        Different environments require different sampling methodologies, but there remain areas where laboratory methodologies could be standardized to allow results to be compared across studies.                                                                          Synthesis and applications                     . We review recently published studies that use e                     DNA                     to monitor aquatic populations, discuss the methodologies used and the application of e                     DNA                     analysis as a survey tool in ecology. We include innovative ideas for how e                     DNA                     can be used for conservation and management citing test cases, for instance, the potential for on-site analyses, including the application of e                     DNA                     analysis to carbon nanotube platforms or laser transmission spectroscopy to facilitate rapid on-site detections. The use of e                     DNA                     monitoring is already being adopted in the                     UK                     for ecological surveys.                                                                           ,                             We review recently published studies that use e               DNA               to monitor aquatic populations, discuss the methodologies used and the application of e               DNA               analysis as a survey tool in ecology. We include innovative ideas for how e               DNA               can be used for conservation and management citing test cases, for instance, the potential for on-site analyses, including the application of e               DNA               analysis to carbon nanotube platforms or laser transmission spectroscopy to facilitate rapid on-site detections. The use of e               DNA               monitoring is already being adopted in the               UK               for ecological surveys.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/ZMDMQE7J/Rees et al. - 2014 - REVIEW The detection of aquatic animal species us.pdf}
}

