@article{rice_etal2018EnvironmentalDNA,
  title = {Environmental {{{\textsc{DNA}}}} Detects a Rare Large River Crayfish but with Little Relation to Local Abundance},
  shorttitle = {Environmental},
  author = {Rice, Christopher J. and Larson, Eric R. and Taylor, Christopher A.},
  year = {2018},
  month = may,
  journal = {Freshwater Biology},
  volume = {63},
  number = {5},
  pages = {443--455},
  issn = {0046-5070, 1365-2427},
  doi = {10.1111/fwb.13081},
  url = {https://onlinelibrary.wiley.com/doi/10.1111/fwb.13081},
  urldate = {2024-11-01},
  abstract = {Abstract                                                                                     Environmental                     DNA                     (                     eDNA                     ) is                     DNA                     extracted from environmental samples (e.g. water) that can be used to infer presence or abundance of species, sometimes with greater sensitivity than conventional sampling methods. Previous                     eDNA                     applications to lotic ecosystems have shown promise in accurately inferring species presence, although studies attempting to estimate species abundance have had mixed results. This may be because                     eDNA                     applications in lotic environments are challenged by directional streamflow, which has the potential to transport detectable                     eDNA                     downstream from its source.                                                                                             Our study sought to evaluate whether results from                     eDNA                     corresponded well with the presence and abundance of the narrowly endemic, large river specialist crayfish                     Faxonius eupunctus                     obtained through a rigorous, well-tested conventional sampling method, or instead, if downstream                     eDNA                     transport in this large river system might overwhelm the effect of local species abundance.                                                                                             We used a species-specific quantitative                     PCR                     (                     qPCR                     ) assay to amplify                     F.~eupunctus                     eDNA                     collected in surface water samples from streams within the Eleven Point River drainage, Arkansas and Missouri,                     U.S.A                     . We estimated                     F.~eupunctus                     eDNA                     detection probabilities and examined relationships between                     eDNA                     detection probability and site-scale variables using a hierarchical occupancy and detection probability modelling framework.                                                                                             Results from                     eDNA                     sampling showed {\texttildelow}90\% agreement relative to our conventional sampling method in estimating                     F.~eupunctus                     presence, although                     eDNA                     failed to detect                     F.~eupunctus                     eDNA                     at two upstream sites where conventional sampling detected                     F.~eupunctus                     individuals. We found a poor relationship between                     F.~eupunctus                     eDNA                     detection probability and local                     F.~eupunctus                     abundance, and a strong relationship between                     eDNA                     detection probability and upstream river distance, which we used as a proxy for the risk of downstream transport of                     eDNA                     from upstream                     F.~eupunctus                     populations.                                                                                             Our results demonstrate                     eDNA                     is a largely reliable tool for estimating the presence of benthic organisms in large, freshwater rivers. However, the likelihood of detecting                     F.~eupunctus                     eDNA                     presence in our study increased as we moved down the stream network, even though local species abundances were greatest at more upstream locations. Therefore, the ability of                     eDNA                     to accurately reflect species presence or abundance in some lotic environments may be hindered by the downstream transport of detectable                     eDNA                     .},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#am},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/BM4KYR9F/Rice et al. - 2018 - Environmental span style=font-variantsmall-caps.pdf}
}

@article{richey_etal2018DistributionPrevalence,
  title = {Distribution and {{Prevalence}} of {{{\emph{Myxobolus}}}}{\emph{ Cerebralis}} in {{Postfire Areas}} of {{Plumas National Forest}}: {{Utility}} of {{Environmental}} {{{\textsc{DNA}}}} {{Sampling}}},
  shorttitle = {Distribution and {{Prevalence}} of {{{\emph{Myxobolus}}}}{\emph{ Cerebralis}} in {{Postfire Areas}} of {{Plumas National Forest}}},
  author = {Richey, Christine A. and Kenelty, Kirsten V. and Van Stone Hopkins, Kristina and Stevens, Brittany N. and Mart{\'i}nez-L{\'o}pez, Beatriz and Barnum, Samantha M. and Hallett, Sascha L. and Atkinson, Stephen D. and Bartholomew, Jerri L. and Soto, Esteban},
  year = {2018},
  month = jun,
  journal = {Journal of Aquatic Animal Health},
  volume = {30},
  number = {2},
  pages = {130--143},
  issn = {0899-7659, 1548-8667},
  doi = {10.1002/aah.10014},
  url = {https://afspubs.onlinelibrary.wiley.com/doi/10.1002/aah.10014},
  urldate = {2024-11-01},
  abstract = {Myxobolus cerebralis is a myxozoan parasite and the etiological agent of whirling disease in salmonids. The parasite's life cycle involves waterborne spores and requires both a salmonid fish and the benthic freshwater oligochaete worm Tubifex tubifex (Oligochaeta: Tubificidae). Wildfires can lead to the erosion of fine sediments into stream channels and have been implicated as promoting environmental conditions that are suitable for the survival and success of T. tubifex, whose presence in turn can affect the prevalence of M. cerebralis. Analysis of environmental DNA (eDNA) has the potential to be a powerful tool for evaluating the presence of microorganisms, for which direct observation is impossible. We investigated the presence of M. cerebralis eDNA in river water and river sediment samples collected from areas affected by recent fire activity in Plumas National Forest, California. We compared eDNA loads in the environment to M. cerebralis infection in T. tubifex and sentinel-exposed Rainbow Trout Oncorhynchus mykiss and the presence of T. tubifex lineages in the same environment. For the latter, we developed a multiplex quantitative PCR assay for detection of T. tubifex lineages I, III, and V. Lineage III T. tubifex and M. cerebralis (eDNA as well as DNA extracted from fish and worm tissues) were detected only in samples obtained from areas affected by the Moonlight wildfire. The association between M. cerebralis infection in sentinel-exposed fish and eDNA detection in environmental samples only approached significance at a P-value of 0.056. However, given the difference in relative effort between the two sampling methods (host versus nonhost environment), our data suggest that eDNA sampling of water and substrate is a promising approach for surveillance of myxozoan fish parasites.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/95FWTSZV/Richey et al. - 2018 - Distribution and Prevalence of Myxobolus cerebr.pdf}
}

