@article{sansom_sassoubre2017EnvironmentalDNA,
  title = {Environmental {{DNA}} ({{eDNA}}) {{Shedding}} and {{Decay Rates}} to {{Model Freshwater Mussel eDNA Transport}} in a {{River}}},
  author = {Sansom, Brandon J. and Sassoubre, Lauren M.},
  year = {2017},
  month = dec,
  journal = {Environmental Science \& Technology},
  volume = {51},
  number = {24},
  pages = {14244--14253},
  issn = {0013-936X, 1520-5851},
  doi = {10.1021/acs.est.7b05199},
  url = {https://pubs.acs.org/doi/10.1021/acs.est.7b05199},
  urldate = {2024-11-01},
  abstract = {Freshwater mussels are vital components of stream ecosystems, yet remain threatened. Thus, timely and accurate species counts are critical for proper conservation and management. Mussels live in stream sediments and can be challenging to survey given constraints related to water depth, flow, and time of year. The use of environmental DNA (eDNA) to monitor mussel distributions and diversity is a promising tool. Before it can be used as a monitoring tool, however, we need to know how much eDNA mussels shed into their environment and how long the eDNA persists. Here, we present a novel application of eDNA to estimate both the presence/absence and abundance of a freshwater mussel species, Lampsilis siliquoidea. The eDNA shedding and decay rates reported within are the first for freshwater mussels. We determined that eDNA shedding was statistically similar across mussel densities, but that first-order decay constants varied between experimental treatments. Finally, we effectively modeled downstream transport of eDNA and present a model that can be used as a complementary tool to estimate mussel density. Our results suggest that eDNA has the potential to be a complementary tool to survey mussels and enhance current efforts to monitor and protect freshwater mussel biodiversity.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/5GT74IQ2/Sansom and Sassoubre - 2017 - Environmental DNA (eDNA) Shedding and Decay Rates .pdf}
}

