@article{scrivener_etal1994JuvenileChinook,
  title = {Juvenile {{Chinook Salmon}} ({{Oncorhynchus}} Tshawytscha) {{Utilization}} of {{Hawks Creek}}, a {{Small}} and {{Nonnatal Tributary}} of the {{Upper Fraser River}}},
  author = {Scrivener, J. Charles and Brown, Thomas G. and Andersen, Bruce C.},
  year = {1994},
  month = may,
  journal = {Canadian Journal of Fisheries and Aquatic Sciences},
  volume = {51},
  number = {5},
  pages = {1139--1146},
  publisher = {NRC Research Press},
  issn = {0706-652X},
  doi = {10.1139/f94-113},
  url = {https://cdnsciencepub.com/doi/abs/10.1139/f94-113},
  urldate = {2024-08-24},
  abstract = {Hawks Creek, a small tributary of the upper Fraser River where there is no record of spawning salmonids, was examined from May to November 1990 for the presence of juvenile salmonids. Age 0+ wild chinook salmon (Oncorhynchus tshawytscha) appeared in May, increased to peak densities of 0.76 fish{$\bullet$}m-2 in late August, and were absent in November. Average residence time of marked chinook juveniles was 9{\enspace}d. Duration of residency was size dependent, as small fish remained longer than large fish. Rainbow trout (Oncorhynchus mykiss) and mountain whitefish (Prosopium williamsoni) also had a short residency (mean{\enspace}={\enspace}21{\enspace}d). We suggest that unsuitable rearing conditions (e.g., sediment load) in the mainstem Fraser River during the spring and summer favour the use of small, clear, and nonnatal tributaries by juvenile salmonids. Physical and biological explanations are proposed for their temporary use. These salmonid refuge habitats need to be protected.}
}

