@article{shu_etal2020StandardsMethods,
  title = {Standards for {{Methods Utilizing Environmental DNA}} for {{Detection}} of {{Fish Species}}},
  author = {Shu, Lu and Ludwig, Arne and Peng, Zuogang},
  year = {2020},
  month = mar,
  journal = {Genes},
  volume = {11},
  number = {3},
  pages = {296},
  issn = {2073-4425},
  doi = {10.3390/genes11030296},
  url = {https://www.mdpi.com/2073-4425/11/3/296},
  urldate = {2024-11-01},
  abstract = {Environmental DNA (eDNA) techniques are gaining attention as cost-effective, non-invasive strategies for acquiring information on fish and other aquatic organisms from water samples. Currently, eDNA approaches are used to detect specific fish species and determine fish community diversity. Various protocols used with eDNA methods for aquatic organism detection have been reported in different eDNA studies, but there are no general recommendations for fish detection. Herein, we reviewed 168 papers to supplement and highlight the key criteria for each step of eDNA technology in fish detection and provide general suggestions for eliminating detection errors. Although there is no unified recommendation for the application of diverse eDNA in detecting fish species, in most cases, 1 or 2 L surface water collection and eDNA capture on 0.7-{\textmu}m glass fiber filters followed by extraction with a DNeasy Blood and Tissue Kit or PowerWater DNA Isolation Kit are useful for obtaining high-quality eDNA. Subsequently, species-specific quantitative polymerase chain reaction (qPCR) assays based on mitochondrial cytochrome b gene markers or eDNA metabarcoding based on both 12S and 16S rRNA markers via high-throughput sequencing can effectively detect target DNA or estimate species richness. Furthermore, detection errors can be minimized by mitigating contamination, negative control, PCR replication, and using multiple genetic markers. Our aim is to provide a useful strategy for fish eDNA technology that can be applied by researchers, advisors, and managers.},
  copyright = {https://creativecommons.org/licenses/by/4.0/},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/ZSK5DNUT/Shu et al. - 2020 - Standards for Methods Utilizing Environmental DNA .pdf}
}

