@article{sigsgaard_etal2015Monitoringextinct,
  title = {Monitoring the Near-Extinct {{European}} Weather Loach in {{Denmark}} Based on Environmental {{DNA}} from Water Samples},
  author = {Sigsgaard, Eva Egelyng and Carl, Henrik and M{\o}ller, Peter Rask and Thomsen, Philip Francis},
  year = {2015},
  month = mar,
  journal = {Biological Conservation},
  volume = {183},
  pages = {46--52},
  issn = {00063207},
  doi = {10.1016/j.biocon.2014.11.023},
  url = {https://linkinghub.elsevier.com/retrieve/pii/S0006320714004480},
  urldate = {2024-11-01},
  abstract = {The European weather loach (Misgurnus fossilis) represents one of many European freshwater fishes in decline. Efficient monitoring is essential if conservation efforts are to be successful, but due to the species' cryptic biology, traditional monitoring methods currently in use are inefficient, time consuming and likely prone to non-detection error. Here, we investigate the usefulness of environmental DNA (eDNA) monitoring as an alternative or supplementary method for surveying the Danish weather loach population, which is presumed to consist primarily of a single group of no more than 50 individuals. In 2008, the majority of historical Danish localities were surveyed, using traditional fishing techniques. We then applied eDNA methods to a number of these, as well as other potential localities. We successfully detected the weather loach at multiple sites in the single known remaining locality; a result that was later confirmed when local managers caught eight live specimens. Furthermore, the eDNA method indicated presence of the weather loach in another historical locality, where the species has not been observed since 1995. At the remaining localities, weather loach eDNA was not detected, providing further evidence for its absence. Importantly, the eDNA survey required less effort in person-hours and lower costs than the traditional fishing survey. This study confirms that eDNA monitoring is a valid supplement to traditional monitoring methods currently applied to monitor rare freshwater fishes. We propose that by providing reliable distribution data at lower cost and limited effort, the eDNA method can allow for increased management efficiency of endangered freshwater species such as the European weather loach. {\'O} 2015 The Authors. Published by Elsevier Ltd. This is an open access article under the CC BY-NC-ND license (http://creativecommons.org/licenses/by-nc-nd/4.0/).},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/7PNJLEMJ/Sigsgaard et al. - 2015 - Monitoring the near-extinct European weather loach.pdf}
}

