@article{takahara_etal2013UsingEnvironmental,
  title = {Using {{Environmental DNA}} to {{Estimate}} the {{Distribution}} of an {{Invasive Fish Species}} in {{Ponds}}},
  author = {Takahara, Teruhiko and Minamoto, Toshifumi and Doi, Hideyuki},
  editor = {Consuegra, Sofia},
  year = {2013},
  month = feb,
  journal = {PLoS ONE},
  volume = {8},
  number = {2},
  pages = {e56584},
  issn = {1932-6203},
  doi = {10.1371/journal.pone.0056584},
  url = {https://dx.plos.org/10.1371/journal.pone.0056584},
  urldate = {2024-11-01},
  abstract = {Knowledge of the presence of an invasive species is critical to monitoring the sustainability of communities and ecosystems. Environmental DNA (eDNA), DNA fragments that are likely to be bound to organic matters in the water or in shed cells, has been used to monitor the presence of aquatic animals. Using an eDNA-based method, we estimated the presence of the invasive bluegill sunfish, Lepomis macrochirus, in 70 ponds located in seven locales on the Japanese mainland and on surrounding islands. We quantified the concentration of DNA copies in a 1 L water sample using quantitative real-time polymerase chain reaction (qPCR) with a primer/probe set. In addition, we visually observed the bluegill presence in the ponds from the shoreline. We detected bluegill eDNA in all the ponds where bluegills were observed visually and some where bluegills were not observed. Bluegills were also less prevalent on the islands than the mainland, likely owing to limited dispersal and introduction by humans. Our eDNA method simply and rapidly detects the presence of this invasive fish species with less disturbance to the environment during field surveys than traditional methods.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/UQWL3D8F/Takahara et al. - 2013 - Using Environmental DNA to Estimate the Distributi.pdf}
}

@article{takahashi_etal2018Seasonaldiel,
  title = {Seasonal and Diel Signature of Eastern Hellbender Environmental {{DNA}}},
  author = {Takahashi, Mizuki K. and Meyer, Mark J. and Mcphee, Carolyn and Gaston, Jordan R. and Venesky, Matthew D. and Case, Brian F.},
  year = {2018},
  month = jan,
  journal = {The Journal of Wildlife Management},
  volume = {82},
  number = {1},
  pages = {217--225},
  issn = {0022-541X, 1937-2817},
  doi = {10.1002/jwmg.21349},
  url = {https://wildlife.onlinelibrary.wiley.com/doi/10.1002/jwmg.21349},
  urldate = {2024-11-01},
  abstract = {Examination of environmental DNA (eDNA) is a non-invasive conservation tool that has been used for the detection of aquatic organisms. When coupled with quantitative polymerase chain reaction (qPCR), eDNA sampling may be used to infer seasonal or diel activities of target species. To survey the status of eastern hellbenders (Cryptobranchus a. alleganiensis), fully aquatic cryptic salamanders of conservation concern, through eDNA analyses, we collected water samples monthly from 13 sites across 8 tributaries of the West Branch Susquehanna River in Pennsylvania, USA, from June through October 2014. We also examined the effects of the breeding season, diel activity, and stream environmental variables (e.g., temp, pH) on eDNA concentration estimates. We repeatedly detected hellbender eDNA from all 4 tributaries known to contain hellbenders, and from downstream sites of 2 of the 4 tributaries without known records of hellbenders. In the tributaries known to contain hellbenders, we observed notable increases in eDNA concentrations during the September breeding season, suggesting possible reproductive events. However, such seasonal eDNA signature was lacking from the eDNA positive sites of the tributaries without known records of hellbenders. There was no difference in eDNA estimates between diurnal and nocturnal samples, indicating that diel activity was inconsequential to eDNA estimates. Our statistical analyses of the eDNA positive sites revealed no effects of the stream variables on eDNA estimates. Yet, the presence of hellbenders was positively associated with stream temperature and negatively with pH. The positive association with temperature was likely to be an artifact of the sampling design, whereas the negative association with pH may indicate negative effects of farming and livestock on hellbenders. Our findings concur with recent studies on the importance of temporal sampling in interpreting eDNA signature in relation to life histories of target species. Further studies are needed to characterize the core habitats of newly found populations for future management of the declining hellbender populations. {\'O} 2017 The Wildlife Society.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/77TS2P58/Takahashi et al. - 2018 - Seasonal and diel signature of eastern hellbender .pdf}
}

