@article{taylor_etal2005SamplingStatistical,
  title = {Sampling and {{Statistical Considerations}} for {{Hydroacoustic Surveys Used}} in {{Estimating Abundance}} of {{Forage Fishes}} in {{Reservoirs}}},
  author = {Taylor, J. Christopher and Thompson, Jessica S. and Rand, Peter S. and Fuentes, Montserrat},
  year = {2005},
  month = feb,
  journal = {North American Journal of Fisheries Management},
  volume = {25},
  number = {1},
  pages = {73--85},
  issn = {0275-5947, 1548-8675},
  doi = {10.1577/M03-241.1},
  url = {https://afspubs.onlinelibrary.wiley.com/doi/10.1577/M03-241.1},
  urldate = {2024-05-10},
  abstract = {An understanding of the spatial distribution of forage fish resources is required to make informed fishery management decisions. We used mobile hydroacoustics to assess the distribution and abundance of forage fish in Badin Lake, a reservoir in central North Carolina. By sampling a series of cross-channel and longitudinal transects and analyzing the data using geostatistics, we characterized both large- and small-scale spatial patterns in forage fish density. Forage fish were observed in higher densities in upstream regions of the reservoir and were seen only in surface waters during July 2000 owing to the existence of a strong thermo--oxycline and in two layers (surface and near bottom) during mixed conditions in December 2001. We observed differences in the scale of patchiness (200--700 m) in forage fish distribution depending on the region of the reservoir where sampling took place, and we infer that these patterns are governed by prevailing limnological conditions. Modeling the spatial variation in the acoustic data using geostatistics resulted in similar average densities (July 2000: 0.56 Ϯ 0.28 [mean Ϯ SD] fish/m2; December 2001: 0.57 Ϯ 0.49 fish/m2) and improvements in the precision of abundance estimates based on approximated variance (July 2000: 7.05 ϫ 106 Ϯ 8.30 ϫ 105 fish; December 2001: 7.07 ϫ 106 Ϯ 2.10 ϫ 106 fish) when compared with arithmetic averaging and extrapolation (July 2000: 0.60 Ϯ 0.61 fish/m2, 7.5 ϫ 106 Ϯ 4.61 ϫ 106 fish; December 2001: 0.60 Ϯ 0.69 fish/m2, 8.00 ϫ 106 Ϯ 6.34 ϫ 106 fish). We found that sampling along longitudinal transects is a more efficient way to characterize the spatial patterns of forage fish distribution and to estimate systemwide abundance and biomass than using data collected with both a longitudinal and cross-channel sampling design in this system.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/IE7DUYZD/Taylor et al. - 2005 - Sampling and Statistical Considerations for Hydroa.pdf}
}

