@article{thalinger_etal2021validationscale,
  title = {A Validation Scale to Determine the Readiness of Environmental {{DNA}} Assays for Routine Species Monitoring},
  author = {Thalinger, Bettina and Deiner, Kristy and Harper, Lynsey R. and Rees, Helen C. and Blackman, Rosetta C. and Sint, Daniela and Traugott, Michael and Goldberg, Caren S. and Bruce, Kat},
  year = {2021},
  month = jul,
  journal = {Environmental DNA},
  volume = {3},
  number = {4},
  pages = {823--836},
  issn = {2637-4943, 2637-4943},
  doi = {10.1002/edn3.189},
  url = {https://onlinelibrary.wiley.com/doi/10.1002/edn3.189},
  urldate = {2024-11-01},
  abstract = {The use of environmental DNA (eDNA) analysis for species monitoring requires rigorous validation---\-from field sampling to the analysis of PCR-\-based results---\-for meaningful application and interpretation. Assays targeting eDNA released by individual species are typically validated with no predefined criteria to answer specific research questions in one ecosystem. Hence, the general applicability of assays, as well as associated uncertainties and limitations, often remain undetermined. The absence of clear guidelines for assay validation prevents targeted eDNA assays from being incorporated into species monitoring and policy; thus, their establishment is essential for realizing the potential of eDNA-b\- ased surveys. We describe the measures and tests necessary for successful validation of targeted eDNA assays and the associated pitfalls to form the basis of guidelines. A list of 122 variables was compiled, consolidated into 14 thematic blocks (e.g., ``in silico analysis''), and arranged on a 5-l\-evel validation scale from ``incomplete'' to ``operational'' with defined minimum validation criteria for each level. These variables were evaluated for 546 published single-\-species assays. The resulting dataset was used to provide an overview of current validation practices and test the applicability of the validation scale for future assay rating. Of the 122 variables, 20\% to 76\% were reported; the majority (30\%) of investigated assays were classified as Level 1 (incomplete), and 15\% did not achieve this first level. These assays were characterized by minimal in silico and in vitro testing, but their share in annually published eDNA assays has declined since 2014. The meta-\-analysis demonstrates the suitability of the 5-\-level validation scale for assessing targeted eDNA assays. It is a user-\-friendly tool to evaluate previously published assays for future research and routine monitoring, while also enabling the appropriate interpretation of results. Finally, it provides guidance on validation and reporting standards for newly developed assays.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/EGZSSPV9/Thalinger et al. - 2021 - A validation scale to determine the readiness of e.pdf}
}

@article{thomas_etal2018eDNASampler,
  title = {{{eDNA Sampler}}: {{A}} Fully Integrated Environmental {{DNA}} Sampling System},
  shorttitle = {{{eDNA Sampler}}},
  author = {Thomas, Austen C. and Howard, Jesse and Nguyen, Phong L. and Seimon, Tracie A. and Goldberg, Caren S.},
  editor = {Golding, Nick},
  year = {2018},
  month = jun,
  journal = {Methods in Ecology and Evolution},
  volume = {9},
  number = {6},
  pages = {1379--1385},
  issn = {2041-210X, 2041-210X},
  doi = {10.1111/2041-210X.12994},
  url = {https://besjournals.onlinelibrary.wiley.com/doi/10.1111/2041-210X.12994},
  urldate = {2024-11-01},
  abstract = {Abstract                                                                Species monitoring using environmental DNA (eDNA) is a powerful new technique for natural resource scientists and the number of research groups employing eDNA detection is growing rapidly. However, current eDNA sampling technologies consist mainly of do-it-yourself solutions, and the lack of purpose-built sampling equipment is limiting the efficiency and standardization of eDNA studies.                                                     Here, we describe the first fully integrated sampling system (Smith-Root eDNA Sampler) designed by a team of molecular ecologists and engineers for high-throughput eDNA sample collection. It consists of a backpack portable pump that integrates sensor feedback, a pole extension with remote pump controller, custom-made filter housings in single-use packets for each sampling site and onboard sample storage. The system is optimized for sampling speed and replicability, while minimizing risk of contamination.                                                     We present an example pilot study designed to identify optimal eDNA Sampler system parameter values (i.e. pump pressure, flow rate, filter pore size, sample volume) in a new sampling environment. We identified a peak in filtration efficiency at a flow rate threshold of 1.0~L/m, and found that 5~{$\mu$}m filters captured significantly more target eDNA than 1~{$\mu$}m filters. Results also suggest that high filtration pressures may reduce eDNA retention, which implies that pressure should be standardized to avoid biasing detection data.                                                     Similar to the technological evolution of backpack electrofishers, eDNA sampling technology is in the process of transitioning from a nascent phase to professionally engineered research tools. Such innovations will be essential as eDNA monitoring becomes one of the industry standard methods used for species detection and management.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/392X6SCT/Thomas et al. - 2018 - eDNA Sampler A fully integrated environmental DNA.pdf}
}

