@article{thomas_etal2019selfpreservingpartially,
  title = {A Self-preserving, Partially Biodegradable {{eDNA}} Filter},
  author = {Thomas, Austen C. and Nguyen, Phong L. and Howard, Jesse and Goldberg, Caren S.},
  editor = {Jentoft, Sissel},
  year = {2019},
  month = aug,
  journal = {Methods in Ecology and Evolution},
  volume = {10},
  number = {8},
  pages = {1136--1141},
  issn = {2041-210X, 2041-210X},
  doi = {10.1111/2041-210X.13212},
  url = {https://besjournals.onlinelibrary.wiley.com/doi/10.1111/2041-210X.13212},
  urldate = {2024-11-01},
  abstract = {Abstract                                                                eDNA studies often rely on water filtration in the field and immediate sample preservation to prevent DNA degradation during sample transport. However, filter membrane transfer steps for preservation can increase risk of sample contamination and the reliance on typical single-use filter housings produces significant plastic waste.                                                     We created a new eDNA filter housing (compatible with any suction pump) partially comprised of a biodegradable, hydrophilic material that functions to automatically preserve captured eDNA via desiccation---no filter membrane transfer steps, no chemical or cold storage required.                                                     We tested the self-preservation capabilities of the desiccating filter housings by filtering replicate samples in an eDNA mesocosm study and compared with ethanol preservation. Self-preserving filters were placed back into original packaging for storage, and samples for both preservation methods were kept at room temperature until extraction at prescribed time points (11~days, 18~days, 25~days, 32~days, 60~days, 88~days, 172~days) post-filtration. Paired field samples were also collected from six pond locations targeting an additional species to demonstrate field performance.                                                                          Quantitative PCR results from the mesocosm study indicated that both methods effectively preserved eDNA over a 6-month storage period, with the self-preserving filters yielding slightly more target DNA on average (SQ~=~329 copies) than ethanol-preserved samples (SQ~=~288 copies) (                     F                     1,38                     ~=~4.050,                     p                     ~=~0.051). Neither method showed signs of degradation after 172~days. Results from field sampling indicated a larger difference between preservation methods, with the self-preserving filters containing approximately 2X the eDNA of ethanol-preserved samples on average (paired                     t                     test,                     p                     ~=~0.020).                                                                        These data suggest that self-preserving eDNA filter housings are a viable alternative to standard ethanol preservation methods and may provide higher detection sensitivity in some circumstances. The new filter housings should also help in reducing the risk of sample contamination, minimize protocol steps and result in less plastic waste. Such innovations are important to assure eDNA data quality and to help in facilitating the inclusion of non-expert sample collectors (e.g. citizen scientists) in research programs.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/UI3BXST5/Thomas et al. - 2019 - A self‐preserving, partially biodegradable eDNA fi.pdf}
}

@article{thompson2004FishHabitat,
  title = {Fish {{Habitat}} in {{Freshwater Streams}}},
  author = {Thompson, Lisa},
  year = {2004},
  month = jan,
  abstract = {The habitat requirements of fish in streams are in many ways similar to those of humans in our own environment. Fish need a place to live and reproduce, oxygen, tolerable temperatures, food, and clean water free of excess sediment or pollutants. The existence of good fish habitat is dependent on a number of factors, such as geology , climate, water flow, the absence of barriers to upstream or downstream movement, habitat structure (pools, riffles, shelter), water quality, the presence of sufficient food, and the lack of excessive numbers of predators and competitors. This publication provides information on how these factors affect fish, with particular attention given to anadromous salmonids. Salmonids are fish in the taxonomic family Salmonidae, including the many species of salmon and trout (Moyle and Cech 2000). Salmonids present in California include Chinook, coho, chum, pink, and kokanee salmon, steel-head, char, rainbow trout, brook trout, and brown trout. Anadromous refers to fish that are born in fresh water, migrate to the sea where they grow and mature, and then return to fresh water to spawn.},
  file = {/Users/airvine/Zotero/storage/RY2AAIH8/thompson_2004_fish_habitat_in_freshwater_streams.pdf}
}

