@article{thomsen_etal2012DetectionDiverse,
  title = {Detection of a {{Diverse Marine Fish Fauna Using Environmental DNA}} from {{Seawater Samples}}},
  author = {Thomsen, Philip Francis and Kielgast, Jos and Iversen, Lars L{\o}nsmann and M{\o}ller, Peter Rask and Rasmussen, Morten and Willerslev, Eske},
  editor = {Lin, Senjie},
  year = {2012},
  month = aug,
  journal = {PLoS ONE},
  volume = {7},
  number = {8},
  pages = {e41732},
  issn = {1932-6203},
  doi = {10.1371/journal.pone.0041732},
  url = {https://dx.plos.org/10.1371/journal.pone.0041732},
  urldate = {2024-11-01},
  abstract = {Marine ecosystems worldwide are under threat with many fish species and populations suffering from human overexploitation. This is greatly impacting global biodiversity, economy and human health. Intriguingly, marine fish are largely surveyed using selective and invasive methods, which are mostly limited to commercial species, and restricted to particular areas with favourable conditions. Furthermore, misidentification of species represents a major problem. Here, we investigate the potential of using metabarcoding of environmental DNA (eDNA) obtained directly from seawater samples to account for marine fish biodiversity. This eDNA approach has recently been used successfully in freshwater environments, but never in marine settings. We isolate eDNA from K-litre seawater samples collected in a temperate marine ecosystem in Denmark. Using next-generation DNA sequencing of PCR amplicons, we obtain eDNA from 15 different fish species, including both important consumption species, as well as species rarely or never recorded by conventional monitoring. We also detect eDNA from a rare vagrant species in the area; European pilchard (Sardina pilchardus). Additionally, we detect four bird species. Records in national databases confirmed the occurrence of all detected species. To investigate the efficiency of the eDNA approach, we compared its performance with 9 methods conventionally used in marine fish surveys. Promisingly, eDNA covered the fish diversity better than or equal to any of the applied conventional methods. Our study demonstrates that even small samples of seawater contain eDNA from a wide range of local fish species. Finally, in order to examine the potential dispersal of eDNA in oceans, we performed an experiment addressing eDNA degradation in seawater, which shows that even small (100-bp) eDNA fragments degrades beyond detectability within days. Although further studies are needed to validate the eDNA approach in varying environmental conditions, our findings provide a strong proof-ofconcept with great perspectives for future monitoring of marine biodiversity and resources.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/P9X3GBPX/Thomsen et al. - 2012 - Detection of a Diverse Marine Fish Fauna Using Env.pdf}
}

@article{thomsen_etal2012Monitoringendangered,
  title = {Monitoring Endangered Freshwater Biodiversity Using Environmental {{DNA}}},
  author = {Thomsen, Philip Francis and Kielgast, Jos and Iversen, Lars L. and Wiuf, Carsten and Rasmussen, Morten and Gilbert, M. Thomas P and Orlando, Ludovic and Willerslev, Eske},
  year = {2012},
  month = jun,
  journal = {Molecular Ecology},
  volume = {21},
  number = {11},
  pages = {2565--2573},
  issn = {0962-1083, 1365-294X},
  doi = {10.1111/j.1365-294X.2011.05418.x},
  url = {https://onlinelibrary.wiley.com/doi/10.1111/j.1365-294X.2011.05418.x},
  urldate = {2024-11-01},
  abstract = {Freshwater ecosystems are among the most endangered habitats on Earth, with thousands of animal species known to be threatened or already extinct. Reliable monitoring of threatened organisms is crucial for data-driven conservation actions but remains a challenge owing to nonstandardized methods that depend on practical and taxonomic expertise, which is rapidly declining. Here, we show that a diversity of rare and threatened freshwater animals---representing amphibians, fish, mammals, insects and crustaceans---can be detected and quantified based on DNA obtained directly from small water samples of lakes, ponds and streams. We successfully validate our findings in a controlled mesocosm experiment and show that DNA becomes undetectable within 2 weeks after removal of animals, indicating that DNA traces are near contemporary with presence of the species. We further demonstrate that entire faunas of amphibians and fish can be detected by high-throughput sequencing of DNA extracted from pond water. Our findings underpin the ubiquitous nature of DNA traces in the environment and establish environmental DNA as a tool for monitoring rare and threatened species across a wide range of taxonomic groups.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/UPS42JGD/Thomsen et al. - 2012 - Monitoring endangered freshwater biodiversity usin.pdf}
}

