@article{tillotson_etal2018Concentrationsenvironmentala,
  title = {Concentrations of Environmental {{DNA}} ({{eDNA}}) Reflect Spawning Salmon Abundance at Fine Spatial and Temporal Scales},
  author = {Tillotson, Michael D. and Kelly, Ryan P. and Duda, Jeffrey J. and Hoy, Marshal and Kralj, James and Quinn, Thomas P.},
  year = {2018},
  month = apr,
  journal = {Biological Conservation},
  volume = {220},
  pages = {1--11},
  issn = {00063207},
  doi = {10.1016/j.biocon.2018.01.030},
  url = {https://linkinghub.elsevier.com/retrieve/pii/S0006320717313277},
  urldate = {2024-11-01},
  abstract = {Developing fast, cost-effective assessments of wild animal abundance is an important goal for many researchers, and environmental DNA (eDNA) holds much promise for this purpose. However, the quantitative relationship between species abundance and the amount of DNA present in the environment is likely to vary substantially among taxa and with ecological context. Here, we report a strong quantitative relationship between eDNA concentration and the abundance of spawning sockeye salmon in a small stream in Alaska, USA, where we took temporally- and spatially-replicated samples during the spawning period. This high-resolution dataset suggests that (1) eDNA concentrations vary significantly day-to-day, and likely within hours, in the context of the dynamic biological event of a salmon spawning season; (2) eDNA, as detected by species-specific quantitative PCR probes, seems to be conserved over short distances (tens of meters) in running water, but degrade quickly over larger scales (ca. 1.5 km); and (3) factors other than the mere presence of live, individual fish --- such as location within the stream, live/dead ratio, and water temperature --- can affect the eDNA-biomass correlation in space or time. A multivariate model incorporating both biotic and abiotic variables accounted for over 75\% of the eDNA variance observed, suggesting that where a system is well-characterized, it may be possible to predict species' abundance from eDNA surveys, although we underscore that species- and system-specific variables are likely to limit the generality of any given quantitative model. Nevertheless, these findings provide an important step toward quantitative applications of eDNA in conservation and management.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/XW8C7J74/Tillotson et al. - 2018 - Concentrations of environmental DNA (eDNA) reflect.pdf}
}

