@article{turner_etal2014Particlesize,
  title = {Particle Size Distribution and Optimal Capture of Aqueous Macrobial {{{\textsc{eDNA}}}}},
  shorttitle = {Particle Size Distribution and Optimal Capture of Aqueous Macrobial},
  author = {Turner, Cameron R. and Barnes, Matthew A. and Xu, Charles C. Y. and Jones, Stuart E. and Jerde, Christopher L. and Lodge, David M.},
  editor = {Gilbert, M.},
  year = {2014},
  month = jul,
  journal = {Methods in Ecology and Evolution},
  volume = {5},
  number = {7},
  pages = {676--684},
  issn = {2041-210X, 2041-210X},
  doi = {10.1111/2041-210X.12206},
  url = {https://besjournals.onlinelibrary.wiley.com/doi/10.1111/2041-210X.12206},
  urldate = {2024-11-01},
  abstract = {Summary                                                                                     Using environmental DNA (                     eDNA                     ) to detect aquatic macroorganisms is a new survey method with broad applicability. However, the origin, state and fate of aqueous macrobial                     eDNA                     -- which collectively determine how well                     eDNA                     can serve as a proxy for directly observing organisms and how                     eDNA                     should be captured, purified and assayed -- are poorly understood.                                                                                             The size of aquatic particles provides clues about their origin, state and fate. We used sequential filtration size fractionation to measure the particle size distribution (PSD) of macrobial                     eDNA                     , specifically Common Carp (hereafter referred to as Carp)                     eDNA                     . We compared it to the PSDs of total                     eDNA                     (from all organisms) and suspended particle matter (SPM). We quantified Carp mitochondrial                     eDNA                     using a custom                     qPCR                     assay, total                     eDNA                     with fluorometry and SPM with gravimetric analysis.                                                                                             In a lake and a pond, we found Carp                     eDNA                     in particles from {$>$}180 to {$<$}0{$\cdot$}2~{$\mu$}m, but it was most abundant from 1 to 10~{$\mu$}m. Total                     eDNA                     was most abundant below 0{$\cdot$}2~{$\mu$}m, and SPM was most abundant above 100~{$\mu$}m. SPM consisted of {$\leq$}0{$\cdot$}1\% total                     eDNA                     , and total                     eDNA                     consisted of {$\leq$}0{$\cdot$}0004\% Carp                     eDNA                     . 0{$\cdot$}2~{$\mu$}m filtration maximized Carp                     eDNA                     capture (85\%~{\textpm}~6\%) while minimizing total (i.e. non-target)                     eDNA                     capture (48\%~{\textpm}~3\%), but filter clogging limited this pore size to a sample volume {$<$}250~                     mL                     . To mitigate this limitation, we estimated a continuous PSD model for Carp                     eDNA                     and derived an equation for calculating isoclines of pore size and water volume that yield equivalent amounts of Carp                     eDNA                     .                                                                                             Our results suggest that aqueous macrobial                     eDNA                     predominantly exists inside mitochondria or cells, and that settling may therefore play an important role in its fate. For optimal                     eDNA                     capture, we recommend 0{$\cdot$}2~{$\mu$}m filtration or a combination of larger pore size and water volume that exceeds the 0{$\cdot$}2~{$\mu$}m isocline.                     In situ                     filtration of large volumes could maximize detection probability when surveying large habitats for rare organisms. Our method for                     eDNA                     particle size analysis enables future research to compare the PSDs of                     eDNA                     from other organisms and environments, and to easily apply them for ecological monitoring.},
  copyright = {http://creativecommons.org/licenses/by-nc/3.0/},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/PM44YWZL/Turner et al. - 2014 - Particle size distribution and optimal capture of .pdf}
}

@article{turner_etal2015Fishenvironmental,
  title = {Fish Environmental {{DNA}} Is More Concentrated in Aquatic Sediments than Surface Water},
  author = {Turner, Cameron R. and Uy, Karen L. and Everhart, Robert C.},
  year = {2015},
  month = mar,
  journal = {Biological Conservation},
  volume = {183},
  pages = {93--102},
  issn = {00063207},
  doi = {10.1016/j.biocon.2014.11.017},
  url = {https://linkinghub.elsevier.com/retrieve/pii/S000632071400442X},
  urldate = {2024-11-01},
  abstract = {Genetic identification of aqueous environmental DNA (eDNA) provides site occupancy inferences for rare aquatic macrofauna that are often easier to obtain than direct observations of organisms. This relative ease makes eDNA sampling a valuable tool for conservation biology. Research on the origin, state, transport, and fate of eDNA shed by aquatic macrofauna is needed to describe the spatiotemporal context for eDNA-based occupancy inferences and to guide eDNA sampling design. We tested the hypothesis that eDNA is more concentrated in surficial sediments than in surface water by measuring the concentration of aqueous and sedimentary eDNA from an invasive fish, bigheaded Asian carp (Hypophthalmichthys spp.), in experimental ponds and natural rivers. We modified a simple, low-cost DNA extraction method to yield inhibitor-free eDNA from both sediment and water samples. Carp eDNA was 8--1800 times more concentrated per gram of sediment than per milliliter of water and was detected in sediments up to 132 days after carp removal -- five times longer than any previous reports of macrobial eDNA persistence in water. These results may be explained by particle settling and/or retarded degradation of sedimentadsorbed DNA molecules. Compared to aqueous eDNA, sedimentary eDNA could provide a more abundant and longer-lasting source of genetic material for inferring current-or-past site occupancy by aquatic macrofauna, particularly benthic species. However, resuspension and transport of sedimentary eDNA could complicate the spatiotemporal inferences from surface water sampling, which is currently the predominant eDNA-based approach. We discuss these implications in the context of conservation-oriented monitoring in aquatic ecosystems.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/YYBRYB9F/Turner et al. - 2015 - Fish environmental DNA is more concentrated in aqu.pdf}
}

