@article{whipple_viers2019Couplinglandscapes,
  title = {Coupling Landscapes and River Flows to Restore Highly Modified Rivers},
  author = {Whipple, A. A. and Viers, J. H.},
  year = {2019},
  journal = {Water Resources Research},
  volume = {55},
  number = {6},
  pages = {4512--4532},
  issn = {1944-7973},
  doi = {10.1029/2018WR022783},
  url = {https://onlinelibrary.wiley.com/doi/abs/10.1029/2018WR022783},
  urldate = {2024-03-15},
  abstract = {Modifications to landscapes and flow regimes of rivers have altered the function, biodiversity, and productivity of freshwater ecosystems globally. Reestablishing geomorphological and hydrological conditions necessary to sustain ecosystems is a central challenge for restoration within highly altered systems. Meeting this challenge requires simultaneously addressing multiple and interacting stressors within the context of irreversible changes and socio-economic constraints. Traditionally, river restoration approaches either physically change the landscape or channel (channel-floodplain manipulation) or adjust hydrology (environmental flows), and such actions are often independent. We juxtapose these two subfields of river restoration, which have undergone parallel transformations, from goals of reproducing static representations of form and flow regime to goals of reestablishing processes. The parallel transformations have generated shared ideas, which point to benefits of coupling channel-floodplain manipulation and environmental flow actions to achieve process-based goals. Such coupling supports comprehensive river restoration efforts aimed at supporting resilient ecosystems within human dominated landscapes in a nonstationary climate. We identify four elements of coupled approaches for restoring highly modified rivers: (1) identify physical and ecological process potential given interactive effects of altered landscapes and flows; (2) consider capacity for sustaining identified processes under potential future change; (3) model alternatives for coupled restoration actions to support identified processes; and (4) evaluate alternatives using metrics representing integrative effects of coupled actions. We suggest these emergent elements contribute to the development of standard practices for restoring highly modified rivers and encourage an increasing number and quality of coupled applications.},
  langid = {english},
  keywords = {environmental flows,habitat restoration,highly modified rivers,hydrogeomorphology,process based,river restoration},
  file = {/Users/airvine/Zotero/storage/6AFQPCNB/whipple_viers_2019_coupling_landscapes_and_river_flows_to_restore_highly_modified_rivers.pdf;/Users/airvine/Zotero/storage/NS8GKXXE/2018WR022783.html}
}

@misc{WhirlingDiseaseColoradosTrout,
  title = {Whirling {{Disease}} and {{Colorado}}'s {{Trout}}},
  url = {https://cpw.cvlcollections.org/items/show/493},
  urldate = {2024-11-08},
  abstract = {About Whirling DiseaseMyxobolus cerebralis, a metazoan parasite, can cause a serious affliction in some species of trout and salmon known as whirling disease. The water-borne parasite may not directly kill trout, but severely infected youngtrout often develop debilitating deformities of the skull and spinal column or display the erratic tail-chasing behavior from which the disease gets its name. Eventually, heavily infected young fish may die.  The parasitic disease probably originated in{\dots}},
