@book{wilson_wright2014Usingenvironmental,
  title = {Using Environmental {{DNA}} ({{eDNA}}) as a Tool in Risk-Based Decision-Making},
  author = {Wilson, Chris and Wright, Elizabeth},
  year = {2014},
  publisher = {Ontario Ministry of Natural Resources},
  address = {Peterborough, Ontario},
  abstract = {Environmental DNA (eDNA) is the genetic material that can be detected by sampling the non-living environment (e.g., water, soil) to provide information about what organisms may be, are, or were recently present. eDNA testing provides a unique opportunity to detect species at lower abundances than using conventional sampling approaches. A confirmed positive detection of eDNA means DNA from the species was present at that location at the time the sample was collected, but provides no information about the source of the DNA (i.e., live or dead animal, animal part, etc). Ongoing studies at MNR's Codrington Research Facility are helping to demonstrate the usefulness and limitations of the eDNA tool, and have confirmed species specificity as well as biomass and temporal sensitivity of eDNA detection. When used to provide information about invasive species and species at risk, eDNA detections can help target conventional sampling methods to increase the probability of capturing species of interest. Detections provide new information that was previously not available, helping to reduce the uncertainty associated with natural resource management decisions regarding the presence of a target species},
  isbn = {978-1-4606-1036-7},
  langid = {english},
  annotation = {OCLC: 894028572},
