  abstract = {Designing eDNA tools to detect and quantify rare species includes inherent assumptions about the spatial distribution of the organism, spatial nature of eDNA dynamics, and the real-world performance of alternate assays under field conditions. Here, we use cage experiments with small numbers of Atlantic salmon (Salmo salar), to reveal that eDNA detection rates and eDNA quantities follow a predictable, but nonlinear relationship with distance from a point source. In contrast to the common assumption of consistent eDNA degradation moving away from a source, eDNA detections and concentrations increased up to roughly 70 m downstream before declining steadily. We apply our eDNA distance functions to selection of stream sampling intervals for detecting fish without known locations and find that even a single juvenile salmon can be reliably detected with intervals up to 400 m spacing. Finally, we show that two different qPCR eDNA assays provide very different detection probabilities in nature despite similar efficiency in laboratory testing, demonstrating the importance of experimentally assessing assay efficiencies in the wild as well as the capacity for multiplexing as a strategy to ensure high detection efficiency when monitoring rare species.},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/4USMNPXP/Wood et al. - 2020 - Experimental assessment of optimal lotic eDNA samp.pdf}
}

@article{woody_etal2002Cloveoil,
  title = {Clove Oil as an Anaesthetic for Adult Sockeye Salmon: Field Trials},
  shorttitle = {Clove Oil as an Anaesthetic for Adult Sockeye Salmon},
  author = {Woody, C. A. and Nelson, J. and Ramstad, K.},
  year = {2002},
  journal = {Journal of Fish Biology},
  volume = {60},
  number = {2},
  pages = {340--347},
  issn = {1095-8649},
  doi = {10.1111/j.1095-8649.2002.tb00284.x},
  url = {https://onlinelibrary.wiley.com/doi/abs/10.1111/j.1095-8649.2002.tb00284.x},
  urldate = {2024-11-05},
  abstract = {Wild migrating sockeye salmon Oncorhynchus nerka exposed to 20, 50 and 80 mg 1-1 of clove oil could be handled within 3 min, recovered within 10 min, and survived 15 min exposure trials. Fish tested at 110 mg 1-1 did not recover from 15 min exposure trials. Response curves developed for induction and recovery time considered the following predictors: clove oil concentration, sex, fish length and depth. A significant positive dependence was observed between induction time and fish length for 20, 50 and 80 mg 1-1 test concentrations; no dependence was observed between induction time and length at 110 and 140 mg 1-1. Recovery time differed as a function of clove oil concentration, but not fish size. A concentration of 50 mg 1-1 is recommended for anaesthetizing sockeye salmon ranging in length from 400 to 550 mm at water temperatures averaging 9--10{$^\circ$} C.},
  langid = {english},
  keywords = {clove oil,fish anaesthesia,Oncorhynchus nerka,sockeye salmon},
  file = {/Users/airvine/Zotero/storage/I83BQ6WD/j.1095-8649.2002.tb00284.html}
}

