@article{zeale_etal2011TaxonspecificPCR,
  title = {Taxon-specific {{PCR}} for {{DNA}} Barcoding Arthropod Prey in Bat Faeces},
  author = {Zeale, Matt R. K. and Butlin, Roger K. and Barker, Gary L. A. and Lees, David C. and Jones, Gareth},
  year = {2011},
  month = mar,
  journal = {Molecular Ecology Resources},
  volume = {11},
  number = {2},
  pages = {236--244},
  issn = {1755-098X, 1755-0998},
  doi = {10.1111/j.1755-0998.2010.02920.x},
  url = {https://onlinelibrary.wiley.com/doi/10.1111/j.1755-0998.2010.02920.x},
  urldate = {2024-11-01},
  abstract = {The application of DNA barcoding to dietary studies allows prey taxa to be identified in the absence of morphological evidence and permits a greater resolution of prey identity than is possible through direct examination of faecal material. For insectivorous bats, which typically eat a great diversity of prey and which chew and digest their prey thoroughly, DNAbased approaches to diet analysis may provide the only means of assessing the range and diversity of prey within faeces. Here, we investigated the effectiveness of DNA barcoding in determining the diets of bat species that specialize in eating different taxa of arthropod prey. We designed and tested a novel taxon-specific primer set and examined the performance of short barcode sequences in resolving prey species. We recovered prey DNA from all faecal samples and subsequent cloning and sequencing of PCR products, followed by a comparison of sequences to a reference database, provided species-level identifications for 149 {\textfractionsolidus} 207 (72\%) clones. We detected a phylogenetically broad range of prey while completely avoiding detection of nontarget groups. In total, 37 unique prey taxa were identified from 15 faecal samples. A comparison of DNA data with parallel morphological analyses revealed a close correlation between the two methods. However, the sensitivity and taxonomic resolution of the DNA method were far superior. The methodology developed here provides new opportunities for the study of bat diets and will be of great benefit to the conservation of these ecologically important predators.},
  copyright = {http://onlinelibrary.wiley.com/termsAndConditions\#vor},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/6HPKE8SH/Zeale et al. - 2011 - Taxon‐specific PCR for DNA barcoding arthropod pre.pdf}
}

